import Navbar from './Navbar';
import React ,{useEffect,useState} from 'react';
import { useNavigate,useParams,useNavigation} from "react-router-dom";
import { ImageList,ImageListItem,Box,Typography,Link} from '@mui/material';
import ImageListItemBar from '@mui/material/ImageListItemBar';
import Pagination from '@mui/material/Pagination';
let length=3;
  var width = window.innerWidth;
const y= (x)=>{
  if(x<650) return 2;
  else  return 4;
}
const z= (x)=>{
  if(x<650) return "170px";
  else  return "300px";
}
const CompPro = () => {
    const [page, setPage] = React.useState(1);
    const handleChange = (event, value) => {
      setPage(value);
    };
    const searchquery=useParams();
    console.log("search",searchquery);
    
        return ( 
        <Box sx={{
                position:"relative",
            }}>
        <Navbar/>
        <ImageList cols={y(width)}  sx={{ position:"relative",
    }}>
        {itemData.map((item) => (
          <Box key={item.img} sx={{
            borderColor:"red",
            borderWidth:"1px",
            width:z(width),
            }}>
        <Link href="/details/1">
            <ImageListItem  >
                <img
                    src={`${item.img}`}
                    srcSet={`${item.img}`}
                    alt={item.title}
                    loading="lazy"
                    style={{
                      backgroundColor:"gray",
                      height:"200px",
                  }}
                />
        </ImageListItem>
        </Link>
        <p style={{backgroundColor:"white",
          maxHeight :"110px",
          width:z(width),
          overflow: "hidden",
          textOverflow:"..."
            }}> Lorem ipsum dolor sit, amet consectetur adipisicing elit. Odit quod quisquam molestias odio sed! Impedit velit ullam aliquam fugit dolorem, eum nesciunt aliquid expedita exercitationem ea? Natus, error earum. Nobis.
            </p>
        <Stack direction={"row"} gap={2}>
          <p style={{color:"green"}}>15000 Birr</p>
          <p style={{color:"red"}}> <del>4000B</del></p>
          <Button sx={{
            height:"30px"
          }} variant="contained" startIcon={<ShoppingCart />}
          onClick={()=>{
            handleaddCart(item.title)
          }}
          >
           Add
          </Button>
        </Stack>
        
        </Box>
      ))}
    </ImageList>
    <div style={{
        position:"relative",
        marginTop:"10px",
        backgroundColor:"yellow",
      }}> <Pagination count={40} color="primary" page={page} onChange={handleChange} sx={{position:"relative" }}/>
      </div>
        </Box>
     );
}
