import React,{useState,useEffect} from 'react';
import NavbarAdmin from './adminNav';
import { Button, TextField, Typography ,CircularProgress} from '@mui/material';
import { Box } from '@mui/system';
import Autocomplete from '@mui/material/Autocomplete';
import axios from 'axios';
import {useNavigate} from "react-router-dom";
const AddCategory = () => {
    const api=axios.create({
        baseURL:"http://localhost:5000/special"
    });
    const [broadcategory,setbroadcategory]=useState([]);
    const [message,setMessage]=useState("");
    const [pid,setpid]=useState(0);
    const [authorized,setauthorized]= useState(false);
    const [display,setdisplay]=useState(false);
    const [companyname,setCompanyName]=useState('');
    const [name,setName]=useState('');
    const nav=useNavigate();
    useEffect(()=>{
        const api=axios.create({
            baseURL:"http://localhost:5000/special"
        });
        api.get('/categories').then(res=>{
    if(res.status===200)
        console.log(res);
        setbroadcategory(res.data);
        console.log(broadcategory);
        setauthorized(true);
    }).catch(err=>{
    if (err.response) {
        console.log(err);
        nav('/adlogin') 
        
    } else if (err.request) {
        // The client never received a response, and the request was never left
        console.log(err);
        nav('/adlogin') 
        
    } else {
        // Anything else
        // nav('/adlogin') 
        console.log(err);
    }
    });


    },[display])
    useEffect((nav)=>{
        const api=axios.create({
            baseURL:"http://localhost:5000/special"
        });
        api.get('/categories').then(res=>{
    if(res.status===200)
        console.log(res);
        setbroadcategory(res.data);
        console.log(broadcategory);
        setauthorized(true);
    }).catch(err=>{
    if (err.response) {
        console.log(err);
        nav('/adlogin') 
        
    } else if (err.request) {
        // The client never received a response, and the request was never left
        console.log(err);
        nav('/adlogin') 
        
    } else {
        // Anything else
        // nav('/adlogin') 
        console.log(err);
    }
    });

    },[])

const handleSumbit= async(e)=>{
    e.preventDefault();
    setMessage('...loading')
    api.post('/addcategories',
        { companyname
    },
    {
    withCredentials:true
    }
).then(res=>{
    console.log(res.data);
    setCompanyName('');
    setMessage("successfully added !");
}).catch(err=>{
    if (err.response) {
    console.log("response error",err.response);
        
    } else if (err.request) {
        // The client never received a response, and the request was never left
        console.log("request error");
    } else {
        // Anything else
        console.log(err);
        console.log("something went wrong ");
    }
    });
  
  
  }
const handleSumbitBroad= async(e)=>{
    e.preventDefault();
    setMessage('...loading')
    api.post('/addbroudcategories',
        { name,pid
    },
    {
    withCredentials:true
    }
).then(res=>{
    console.log(res.data);
    setCompanyName('');
    setMessage("successfully broad category has been  added !");
}).catch(err=>{
    if (err.response) {
    console.log("response error",err.response);
    setMessage("this category is added before!");
    } else if (err.request) {
        // The client never received a response, and the request was never left
        console.log("request error");
        setMessage("something Went wrong");
    } else {
        // Anything else
        console.log(err);
        console.log("something  sub category is addedwent wrong ");
    }
    });
}
    return ( 
        <>
        {
        authorized && 
        <>
        <NavbarAdmin/>
        <Box sx={{
        position:"absolute",
        top:"100px",
        left:"10%",
        width:"80%",
        ackgroundColor:"white",
    }}>
    <Typography variant="h4" sx={{
        color:"violet",
        fontWeight:"bold",
        }}> {display?"Add Category":"Add BroadCategory"}</Typography>
        <br/>
        <br/>
        <Button variant="outlined" onClick={()=>{
            setdisplay(!display)
        }} >{display?" Changet to Add Category":" change to Add BroadCategory"}</Button>
        <br/>
        { display&&
        <form onSubmit={handleSumbit}>
            <p><b>{message}</b></p>
            <Autocomplete
                id="Region"
                freeSolo={false}
                options={broadcategory.map((option) => option.name)}
                name="category"
                defaultChecked={broadcategory[0].name}
                onChange={(event, newValue) => {
                    let k=broadcategory.length;
                    for(let i=0;i<k;i++){
                        if(broadcategory[i].name===newValue){
                            setpid(newValue);
                            console.log(pid);
                            break;
                        }
                    }
                    
                }}
                renderInput={(params) => <TextField {...params} label="Broadcategory" />}
            />
        <TextField required label="categoryname" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} 
        
        onChange={(e)=>setCompanyName(e.target.value)}
        ></TextField>   
        <br/>
    <br/>   
        <Button type='submit' variant='contained' color="primary" >POST</Button>   
    </form>}
    {/******--------------- Add broad category ------------- */}
        <br/>
        <br/>
        {!display && <form onSubmit={handleSumbitBroad}>
            <p><b>{message}</b></p>
        <TextField required label="Broad category name" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} 
        onChange={(e)=>setName(e.target.value)}
        ></TextField>   
        <br/>
    <br/>   
        <Button type='submit' variant='contained' color="primary" >POST</Button>   
    </form>}

    <br/>
    <br/>
    <br/>
    <div> ------</div>
        </Box>
    </>
    },
    {!authorized && <CircularProgress/>}
    </>
    );
}

export default AddCategory;
