import React,{useState} from 'react';
import axios from "axios";
import MultiImageInput from "react-multiple-image-input";


const EditProduct = () => {
    const crop = {
        unit: "%",
        aspect: 4 / 3,
        width: "100"
      };
    const onSubmit = async (e) => {
      e.preventDefault();
      console.log("imageu")
