    const colors=["red","blue","yellow","green","blueviolet"];
    const [preview,setPreview]=useState(colors[0]);
    return (
        <div>  
            <h1> Title of the Item </h1>
            <Stack direction={"row"} className="photo-list">
                <button className="items" style={{
                    backgroundColor:colors[0]
                }} onClick={()=>{
                    setPreview(colors[0]);
