                    backgroundColor:colors[3]
                }} onClick={()=>{
                    setPreview(colors[3]);
                }} > gett</button>
                <button className="items" style={{
                    backgroundColor:colors[4]
                }} onClick={()=>{
                    setPreview(colors[4]);
                }} > gett</button>
            </Stack>
            <div className="details" >
                <div className="bigPicture" style={
                    {
                        backgroundColor:preview
