import React,{ useState,useEffect }  from 'react';
import NavbarSeller from './navbarSeller';
import {MenuItem,Box,Typography, Button } from '@mui/material';
import TextField from '@mui/material/TextField';
import CameraAltIcon from '@mui/icons-material/CameraAlt';
import Autocomplete from '@mui/material/Autocomplete';
const AddProduct = () => {
  const [selectedFile, setSelectedFile] = useState([]);
  const [preview, setPreview] = useState([]);
  const regions = [
    { title: 'Oromia'},
    { title: 'AddisAbaba'},
    { title: 'Amhara'},
    { title: 'Afar' },
    { title: 'Somali' },
    { title: "Sidama" },
    { title: 'BenishangulGumuz'},
    { title: 'Gambella' },
    { title: "SNNP" },
    { title: 'DireDewa'}
]
  let fileArray = [];
  const uploadMultipleFiles=(e)=>{
    let fileObj = [];
    fileObj.push(e.target.files)
    console.log(fileObj)
    console.log(fileArray)
    for (let i = 0; i < fileObj[0].length; i++) {
        fileArray.push(URL.createObjectURL(fileObj[0][i]))
    }
    setPreview(fileArray)
    console.log(fileArray)

}

    return ( 
    <div>
        <NavbarSeller/>
        <Box 
        sx={{
            position:"absolute",
            left:"10%",
            top:"10%",
            width:"80%",
            backgroundColor:"white",

        }}>
        <Typography variant="h4">AddProduct</Typography>
        <form action="http://localhost:5000/sel/upload" method='POST' encType ="multipart/form-data" >
          <br/><br/>
        <TextField  label="productName" id="fullWidth"  type="text" required name='pname'/>
        <br/><br/>
        <TextField  label="Price" id="fullWidth" type="number" required name='price'/>
        <br/><br/>
        <Autocomplete
        id="Region"
        freeSolo={false}
        options={regions.map((option) => option.title)}
        name="category"
        renderInput={(params) => <TextField {...params} label="Region" />}
      />
        <textarea name="description" id="did" cols="30" rows="10" placeholder='add description'></textarea>
        <label htmlFor="inputPic" style={{
          backgroundColor:"lightblue",
          color:"white",
          width:"120px",
          height:"50px",
          alignItems:"center",
          fontSize:"16px"
        }}>
          <i><CameraAltIcon/> </i>
            Images
        <input type="file" id='inputPic' accept='image/*' onChange={uploadMultipleFiles} required multiple name="image" style={{
          display:"none"
        }} /> 
        </label>  
        <br></br>
        <input type="file"  accept='video/*' id='inputVid' name="bema" style={{
          color:"white",
          backgroundColor:"green"
        }} /> 
        <div className="form-group multi-preview">
                    {(preview || []).map(url => (
                        <img key={url} src={url} alt="..." />
                    ))}
                </div>

        <button type="submit" >POST</button>
      
        </form>
       
        </Box>

    </div>
     );
}

export default AddProduct;
