import React,{ useState }  from 'react';
import NavbarSeller from './navbarSeller';
import { TextField,MenuItem,Select,InputLabel,Box,Typography } from '@mui/material';
import FormControl from '@mui/material/FormControl';
import axios from "axios";
// import { useForm } from "react-hook-form";
import MultiImageInput from "react-multiple-image-input";
//----///
const serv=axios.create({
  baseURL:"http://localhost:5000/sel",
  headers:{"Content-Type":"multipart/formdata"}
});

const AddProduct = () => {
  const [category, setCategory] = React.useState('');
  const handleChange = (event) => {
      setCategory(event.target.value);
  };
  const [fileList, setFileList]=useState<FileList | null>(null);
  const handleFileChange = (e) => {
    setFileList(e.target.files);
  };
  const handleSumbit=async(e)=>{
    e.preventDefault();
    const data = new FormData();
    fileList.forEach((file, i) => {
      data.append(`file-${i}`, file, file.name);
    });

    serv.post('/upload',
      data
    ).then(res=>{
      // console.log(res);
    }).catch(err=>{
      if (err.response) {
        console.log("response error");
     
    } else if (err.request) {
        // The client never received a response, and the request was never left
        console.log(" request error something went wrong ");
    } else {
        // Anything else
        console.log("something went wrong ");
    }
    });
    
}
  const crop = {
    unit: "%",
    aspect: 4 / 3,
    width: "100"
  };
    return ( 
    <div>
        <NavbarSeller/>
        <Box sx={{
            position:"absolute",
            left:"10%",
            top:"10%",
            width:"80%",
            backgroundColor:"white",

        }}>
            <Typography variant="h4">AddProduct</Typography>
        <form onSubmit={handleSumbit}>
        <TextField id="outlined-basic" label="name" variant="outlined" />
        <br/>
        <br/>
        <TextField id="outlined-basic" label="price" variant="outlined" />
        <br/>
        <br/>
        <FormControl fullWidth >
        <InputLabel id="demo-simple-select-label">category</InputLabel>
        <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={category}
            label="category"
            onChange={handleChange}
        >
          <MenuItem value="cosmotics">cosmotics</MenuItem>
          <MenuItem value="shoes">shoes</MenuItem>
          <MenuItem value="laptop">laptop</MenuItem>
        </Select>
        <br/>
        <br/>
        </FormControl>
        
        <input type="file" onChange={handleFileChange} multiple />

        </form>

        </Box>
    </div>
     );
}

export default AddProduct;
