import React,{useEffect, useState} from 'react';
import axios from "axios";
import{  useParams} from 'react-router-dom';
import MultiImageInput from "react-multiple-image-input";
import NavbarSeller from './navbarSeller';
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import ImageListItemBar from '@mui/material/ImageListItemBar';
import ListSubheader from '@mui/material/ListSubheader';
import IconButton from '@mui/material/IconButton';
import {Delete} from '@mui/icons-material';
import { TextField ,Box, Typography, CircularProgress} from '@mui/material';

const EditProduct = ({pid}) => {
  const api=axios.create({
    baseURL:"http://localhost:5000/sel"
  });
    let productDetail;
    const y= (x)=>{
      if(x<600) return 2;
      else  return 4;
    }
    const z= (x)=>{
      if(x<600) return "100%";
      else  return "60%";
    }
    const id=useParams();
    var width = window.innerWidth;
    const itemData = [
      {
        img: 'https://images.unsplash.com/photo-1551963831-b3b1ca40c98e',
        title: 'Breakfast',
        author: '@bkristastucchio',
        rows: 2,
        cols: 2,
        featured: true,
      },
      {
        img: 'https://images.unsplash.com/photo-1551782450-a2132b4ba21d',
        title: 'Burger',
        author: '@rollelflex_graphy726',
      },
      {
        img: 'https://images.unsplash.com/photo-1522770179533-24471fcdba45',
        title: 'Camera',
        author: '@helloimnik',
      },
      {
        img: 'https://images.unsplash.com/photo-1444418776041-9c7e33cc5a9c',
        title: 'Coffee',
        author: '@nolanissac',
        cols: 2,
      },
      {
        img: 'https://images.unsplash.com/photo-1533827432537-70133748f5c8',
        title: 'Hats',
        author: '@hjrc33',
        cols: 2,
      },
      {
        img: 'https://images.unsplash.com/photo-1558642452-9d2a7deb7f62',
        title: 'Honey',
        author: '@arwinneil',
        rows: 2,
        cols: 2,
        featured: true,
      },
      
    ];
    const[products,setproducts]=useState();
    const DeleteSelected=(Imageid)=>{
        let id=Imageid;
        if(id){
          api.delete(`/images`,
          id
          ).then(res=>{
            console.log(res);
            console.log(res.data);
          }).catch(err=>{
            if (err.response) {
              console.log("response error");
              
          } else if (err.request) {
              // The client never received a response, and the request was never left
          
          } else {
              // Anything else
              console.log("something went wrong ");
          }
          });
        }
        
    }
    const[found,setfound]=useState(false);
    const fid=id.pid;
    console.log(fid);
    useEffect(()=>{
      api.get(`/product/:${fid}`,
    ).then(res=>{
      console.log("we are  in this shit!")
      console.log(res);
      console.log(res.data);
      let value=res.data;
      setfound(true);
      setproducts({...value})
    }).catch(err=>{
      if (err.response) {
        console.log("response error");
        
    } else if (err.request) {
        // The client never received a response, and the request was never left
    
    } else {
        // Anything else
        console.log("something went wrong ");
    }
    });
  
    },[])
    
    return ( 
        <>
        {found&&<>
        <NavbarSeller/>
        <ImageList sx={{
          width:z(width)
        }}>
      <ImageListItem key="Subheader" cols={y(width)}>
        <ListSubheader component="div">PHOTOS</ListSubheader>
      </ImageListItem>
      {itemData.map((item) => (
        <ImageListItem key={item.img} sx={{
          width:"200px"
        }}>
          <img
            src={`${item.img}`}
            srcSet={`${item.img}`}
            alt={item.title}
            loading="lazy"
          />
          <ImageListItemBar
            actionIcon={
              <IconButton
                sx={{ color: 'rgba(255, 255, 255, 0.54)' }}
                aria-label={`info about ${item.title}`}
              >
                <Delete/>
              </IconButton>
            }
          />
        </ImageListItem>
      ))}
    </ImageList>
        <br/>
        <br/>
        <Box sx={{
          postion:"relative",
          left:"40px",
          border:"1px",
          borderColor:"black"
        }}>
        <form >
        <TextField
          id="outlined-required"
          label="ProductName"
          defaultValue="initialname"
        />
         <br/>
        <br/>
        <TextField
          type="number"
          id="outlined-required"
          label="price"
          defaultValue="56"
        />
         <br/>
        <br/>
        <TextField
          required
          id="outlined-required"
          label="Description"
          defaultValue="description"
        />
        <p>Description</p>
        <textarea  style={{
          position:"relative",
          marginTop:"5px",
          width:"400px",
          height:"300px"
        }}>
          Description
        </textarea>
        <br />
        <br />
        <button type="submit" >Edit</button>
        </form>
        </Box>
        </>},
        {
          !found&& <CircularProgress/>
        }
        </>
     );
}
 
export default EditProduct;
