import { Component } from "react";
import React from 'react';
import { useState,useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Box, Stack } from "@mui/system";
import {CircularProgress} from "@mui/material";
import { Button, ButtonGroup } from "@mui/material";
import '../css/details.css';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import SendIcon from '@mui/icons-material/Send';
import Navbar from './Navbar';
import axios from "axios";

const Details = () => {
    const width=window.width;
    const pwidth=()=>{
        if(width<600){
            return `${width}`;
        }
        else{
            return "700px";
        }
    }
    const poid=useParams();
    const nav=useNavigate();
    let pid=poid.pid;
    const [open, setOpen] = useState(false);
    const handleClose = () => {
    setOpen(false);
    };
    const api=axios.create({
        baseURL:"http://localhost:5000/details"
    })
    const api1=axios.create({
        baseURL:"http://localhost:5000/custom"
    })
    // const id=useParams('id');
    const [pictures,setpictures]=useState([]);
    const [product,setProduct]=useState({});
    const [preview,setPreview]=useState(0);
    const [ordering,setordering]=useState(false);
    const handleOrder=()=>{
        setordering(true);
        api1.post("/order",{
            pid
        },
        {
            withCredentials:true
        }).then(res=>{
            if(res.status===200){
                setOpen(true);
                setordering(false);
            }
        })
        .catch(err=>{
            if(err.response.status===403){
                nav('/')
            }
        })

    }
    
    useEffect(()=>{
        api.get(`/${pid}`)
        .then(async res=>{
            if(res.status===200){
                let bd=res.data;
                let get={
                    pid:bd.pid,
                    CategoryCid:bd.CategoryCid,
                    SellerSid:bd.SellerSid,
                    description:bd.description,
                    marketprice:bd.marketprice,
                    price:bd.price,
                    pname:bd.pname
                }
                setProduct(get);
                console.log("the product is ",product);
                setpictures([...bd.Pictures]);
                setPreview(bd.Pictures[0].id)
                
            }
            })
            .catch(err=>{
            console.log(err)
            })
    },[])
    return (
        <div>  
            <Navbar/>
            <h3> Details</h3>
            <Box  className="photo-list">
                {
                    pictures.map((item)=>(
                    <div key={item.id}
                    onClick={()=>{
                        setPreview(item.id)
                    }}
                    className="details"
                    style={{
                        height:"60px",
                        width:"60px",
                        position:"relative",
                        marginTop: "10px",
                        display: "inline-block",
                        marginLeft: "5px"
                    }}
                                        >
                    <img
                        height="60px"
                        width="60px"
                        src={`http://localhost:5000/images/${item.id}`}
                        alt={"a"}
                    />
                    </div>
                    ))
                }

            </Box>
            <div className="details" >
                <Stack direction={"row"}>
                <div className="bigPicture" style={{
                            position:"relative",
                            backgroundColor:preview
                        }}>
                        <img
                        style={{
                            height: "380px",
                            width: "350px"
                        }}
                        src={`http://localhost:5000/images/${preview}`}
                        alt={"a"}
                    />
                    </div>
                </Stack>
                <div className="detailWords"> 
                    <h1>{product.pname}</h1>
                    <div>
                    <div style={{
                        // width:"100px",
                        height:"40px",
                        fontWeight:"bold",
                        backgroundColor:"white",
                        borderColor:"red",

                    }}> 
                        <center style={{color:"red"}}>{product.price}</center>
                    </div> 
                    </div>
                    <br></br>
                    <p style={{
                        width:pwidth
                    }}>
                    {product.description}
                    </p>
                </div>
            </div>

            <Button variant="contained" onClick={handleOrder} sx={{
                width:"50%",
                marginTop:"20px"
            }} endIcon={<SendIcon />} disabled={ordering} >
                Order
            </Button>
            <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
    >
        <DialogTitle sx={{
            fontWeight:"bolder"
        }} id="alert-dialog-title">
          {"Harena.com "}
        </DialogTitle>
        <DialogContent>
            <DialogContentText id="alert-dialog-description">
            Thanks for your order!
            Dear customer.Our phone operators will reach you by  phone contact to deliver your product 
            Enjoy our products !
        </DialogContentText>
        </DialogContent>
        <DialogActions sx={{
            backgroundColor:"brown"
        }}>
            <Button sx={{
                color:"white"
            }} onClick={handleClose} autoFocus>
                ok!
            </Button>
        </DialogActions>
      </Dialog>

        </div>
    );
}
export default Details;
