import { Component } from "react";
import React from 'react';
import { useState,useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Box, Stack } from "@mui/system";
import { Button, ButtonGroup } from "@mui/material";
import '../css/details.css';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import SendIcon from '@mui/icons-material/Send';
import Navbar from './Navbar';
import axios from "axios";

const Details = () => {
    const colors=["red","blue","yellow","green","blueviolet"];
    const width=window.width;
    const pwidth=()=>{
        if(width<600){
            return `${width}`;
        }
        else{
            return "700px";
        }
    }
    const poid=useParams();
    console.log("poid" ,poid);
    const nav=useNavigate();
    let pid=poid.pid;
    const [open, setOpen] = useState(false);
    const handleClickOpen = () => {
    setOpen(!open);
    };
    const handleClose = () => {
    setOpen(false);
    };
    console.log("pid",pid);
    const api=axios.create({
        baseURL:"http://localhost:5000/details"
    })
    // const id=useParams('id');
    const itemData = [    
        {
          img: 'https://images.unsplash.com/photo-1551782450-a2132b4ba21d'
        },
        {
          img: 'https://images.unsplash.com/photo-1522770179533-24471fcdba45'
        },
        {
          img: 'https://images.unsplash.com/photo-1444418776041-9c7e33cc5a9c'
        },
        {
          img: 'https://images.unsplash.com/photo-1533827432537-70133748f5c8'
        }
    ]
    const [pictures,setpictures]=useState([]);
    const [preview,setPreview]=useState(itemData[0].img);
    const handleOrder=()=>{
        setOpen(true);
        // api.post("/order",{
        //     pid
        // },
        // {
        //     withCredentials:true
        // }).then(res=>{
        //     if(res.status===200){
                
        //     }
        //     else{
        //         nav('/')
        //     }
        // })

    }
    
    useEffect(()=>{
        api.get(`/${pid}`)
    .then(async res=>{
      if(res.status===200){
        let bd=res.data;
        console.log("the detail is ",bd);
        setpictures([...bd.Pictures])
      }
    })
    .catch(err=>{
      console.log(err)
    })
    },[])
    return (
        <div>  
            <Navbar/>
            <h3> Details</h3>
            <Box  className="photo-list">
                {
                    itemData.map((item)=>(
                    <div key={item.img}
                    onClick={()=>{
                        setPreview(item.img)
                    }}
                    className="details"
                    style={{
                        height:"60px",
                        width:"60px",
                        position:"relative",
                        marginTop: "10px",
                        display: "inline-block",
                        marginLeft: "5px"
                    }}
                                        >
                    <img
                        height="60px"
                        width="60px"
                        src={`${item.img}`}
                        alt={"a"}
                    />
                    </div>
                    ))
                }

            </Box>
            <div className="details" >
                <Stack direction={"row"}>
                <div className="bigPicture" style={{
                            position:"relative",
                            backgroundColor:preview
                        }}>
                        <img
                        style={{
                            height: "380px",
                            width: "350px"
                        }}
                        src={`${preview}`}
                        alt={"a"}
                    />
                    </div>
                </Stack>
                <div className="detailWords"> 
                    <h1> HP Laptop</h1>
                    <hr></hr>
                    <div>
                    <div style={{
                        // width:"100px",
                        height:"40px",
                        fontWeight:"bold",
                        backgroundColor:"white",
                        borderColor:"red",

                    }}> 
                        <center style={{color:"red"}}>Price-  5000</center>
                    </div> 
                    </div>
                    <br></br>
                    <p style={{
                        width:pwidth
                    }}>
                    RAM : 16GB /n
                    STORAGE: 1TB Hard Disk
                    Type: HP LAPTOP 
                    CPU  :Intel core i5 dual core
                    With Graphics card
                    Lorem ipsum dolor sit amet consectetur adipisicing elit. Fugit velit minus vel impedit dolor quos qui delectus?
                    Nulla minima rerum magni numquam nemo autem non quas! Ab suscipit omnis exercitationem!
                    Lorem ipsum dolor sit, amet consectetur adipisicing elit. Neque deleniti, rem adipisci illum tenetur atque ipsum. Veritatis, 
                    quis deleniti earum totam ea dolorem odio vel praesentium amet aut saepe quia?
                    Lorem ipsum dolor sit amet consectetur adipisicing elit. Quaerat, modi eos! Nesciunt a optio earum incidunt excepturi sint sapiente vitae explicabo, nobis,
                    maiores sequi assumenda adipisci, omnis voluptatem dolore eaque.
                    </p>
                </div>
            </div>

            <Button variant="contained" onClick={handleOrder} sx={{
                
                marginTop:"20px"
            }} endIcon={<SendIcon />}>
                Order
            </Button>
            <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
    >
        <DialogTitle sx={{
            fontWeight:"bolder"
        }} id="alert-dialog-title">
          {"Harena.com "}
        </DialogTitle>
        <DialogContent>
            <DialogContentText id="alert-dialog-description">
            Thanks for your order!
            Dear customer.Our phone operators will reach you by  phone contact to deliver your product 
            Enjoy our products !
        </DialogContentText>
        </DialogContent>
        <DialogActions sx={{
            backgroundColor:"brown"
        }}>
            <Button sx={{
                color:"white"
            }} onClick={handleClose} autoFocus>
                ok!
            </Button>
        </DialogActions>
      </Dialog>

        </div>
    );
}
export default Details;
