import { Box, Stack } from '@mui/system';
import Navbar from './Navbar';
import { Avatar, TextField, Typography,Button,CircularProgress } from '@mui/material';
import { Edit } from "@mui/icons-material";
import Autocomplete from '@mui/material/Autocomplete';
import React,{ useState,useEffect } from 'react';
import {useNavigate} from 'react-router-dom';
import axios from 'axios';
const Profile=()=>{
    const [editMode,setEditMode]=useState(false);
    const [normal,setnormal]=useState(true);
    const nav=useNavigate();
    const [profiles,setProfiles]=useState({});
    const [changepassword,setchangepassword]=useState(false);
    const [fname,setfname]=useState('');
    const [lname,setlname]=useState('');
    const [telUname,settelUname]=useState('');
    const [email,setemail]=useState('');
    const [phone,setphone]=useState('');
    const [city,setCity]=useState('');
    const [region,setregion]=useState('');

    const [message,setmessage]=useState('');
    const [pp,setpp]=useState('');
    const [np,setnp]=useState('');
    const [cp,setcp]=useState('');
    const [authorized,setAuthorized]=useState(false);
    const api=axios.create({
        baseURL:"http://localhost:5000/custom"
    }); 
    const handlechangepp=(e)=>{
        e.preventDefault();
        setmessage("...loading")
        api.post('/changepp',{
            fname,lname,telUname,email,phone,city,region
        },
        {
            withCredentials:true
            }
    ).then(res=>{
        if(res.status===200)
        {
            setmessage("successfully updated profile")
        }
        }).catch(err=>{
            if (err.response.status===404) {
                // console.log(err.response)
                nav('/sellerLogin') 
                setmessage("update failed");
            } else if (err.request) {
                setmessage("something went wrong");
            } else {
                setmessage("something went wrong");
            }
      });
    }
    const handlechangepassword=(e)=>{
        e.preventDefault();
        setmessage("...loading")
        if(np!==cp){
            setmessage("password should be the same ")
            return;
        }
        api.post('/changepassword',{
            pp,np,cp
        },{
            withCredentials:true
        }
      ).then(res=>{
        if(res.status===200)
        {
           setmessage("successfully updated")
        }
        }).catch(err=>{
            if (err.response) {
                setmessage("wrong password");
                console.log(err);
        } else if (err.request) {
                setmessage("you are currently offline")
      } else {
          // Anything else
          console.log(err);
          nav('/adlogin') 
        
      }
      });

    }
    useEffect(()=>{
        console.log("running the use effect ");
        api.get('/getprofile',
        {
            withCredentials:true
        }
      ).then(res=>{
        setAuthorized(true);
        if(res.status===200)
        {
