import React,{useEffect,useState} from 'react';
import { useNavigate } from 'react-router-dom';
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import { Box, List,Button } from '@mui/material';
import {ShoppingCart} from '@mui/icons-material';
import { Stack } from '@mui/system';
import Pagination from '@mui/material/Pagination';
import axios from 'axios';
import {Link,Icon} from '@mui/material';
const data = [    
  {
    img: 'https://images.unsplash.com/photo-1551782450-a2132b4ba21d',
    title: 'Burger',
    author: '@rollelflex_graphy726',
  },
  {
    img: 'https://images.unsplash.com/photo-1522770179533-24471fcdba45',
    title: 'Camera',
    author: '@helloimnik',
  },
  {
    img: 'https://images.unsplash.com/photo-1444418776041-9c7e33cc5a9c',
    title: 'Coffee',
    author: '@nolanissac',
    cols: 2,
  },
  {
    img: 'https://images.unsplash.com/photo-1533827432537-70133748f5c8',
    title: 'Hats',
    author: '@hjrc33',
    cols: 2,
  },
  {
    img: 'https://images.unsplash.com/photo-1558642452-9d2a7deb7f62',
    title: 'Honey',
    author: '@arwinneil',
    rows: 2,
    cols: 2,
    featured: true,
  },
  {
    img: 'https://images.unsplash.com/photo-1516802273409-68526ee1bdd6',
    title: 'Basketball',
    author: '@tjdragotta',
  },
  {
    img: 'https://images.unsplash.com/photo-1518756131217-31eb79b20e8f',
    title: 'Fern',
    author: '@katie_wasserman',
  },
  {
    img: 'https://images.unsplash.com/photo-1597645587822-e99fa5d45d25',
    title: 'Mushrooms',
    author: '@silverdalex',
    rows: 2,
    cols: 2,
  },
  {
    img: 'https://images.unsplash.com/photo-1567306301408-9b74779a11af',
    title: 'Tomato basil',
    author: '@shelleypauls',
  },
  {
    img: 'https://images.unsplash.com/photo-1471357674240-e1a485acb3e1',
    title: 'Sea star',
    author: '@peterlaster',
  },
  {
    img: 'https://images.unsplash.com/photo-1551963831-b3b1ca40c98e',
    title: 'Breakfast',
    author: '@bkristastucchio',
    rows: 2,
    cols: 2,
    featured: true,
  },
  {
    img: 'https://images.unsplash.com/photo-1589118949245-7d38baf380d6',
    title: 'Bike',
    author: '@southside_customs',
    cols: 2,
  },
];
const z= (x)=>{
  if(x<600) return "180px";
  else  return "250px";
}
const y= (x)=>{
  if(x<600) return 2;
  else if (x<1000) return 3;
  else  return 5;
}

var width = window.innerWidth;
export default function Feed({subcat}) {
  const bv=[0,1,2];
  const [itemData,setItemData]=useState([]);
  let [find ,setfind]=useState(false);
  const nav=useNavigate();
  const [nopage,setnopage]=useState(1);
  let items=[];
  const [page, setPage] = React.useState(1);
  const handleChange = (event, value) => {
    setPage(value);
  };
  let [products,setProducts]=useState([]);
  const api=axios.create({
    baseURL:"http://localhost:5000/subcategory"
  })
  const handleaddCart=(itemid)=>{
    console.log("The item id is  ",itemid);
      // api.post('/addcart',{
      //   withCredentials:true
      // },
      // )
      // .then(res=>{

      // }).catch(err=>{
      //   if(err.response.status==404){
      //     nav('/')
      //   }
      //   else{
      //     console.log(err);
      //   }
      // })
  }
  const [wait,setwait]=useState(false)
  useEffect(()=>{
    console.log("the subcategory in feed 2 is ",subcat);
    let x=(page-1)*4;
    let array=[];
    let limit=x+4<11?x+4:11;
    // for(;x<limit;x++){
    //   array.push(data[x]);
    // }
    // setItemData([...array]);
    api.get(`/?cname=${subcat}&page=${page}`)
    .then(async res=>{
      if(res.status===200){
        let bd=res.data;
        items=bd;
        setnopage(bd.count);
        setItemData([...bd.rows]);
        // setfind(true);
        console.log("product",products)
        console.log("items",items)
      }
    })
    .catch(err=>{
      console.log(err)
    })
  },[page])
  return (
    <Box flex={6} 
   >
      <Box sx={{
      position:"relative",
      marginTop:"50px",
      // width:"100%",
      // backgroundColor:"green",
  }}>
    
      {itemData.length>0 && <ImageList cols={y(width)}  sx={{ 
        position:"relative", 

    }}>
        {itemData.map((item) => (
          <Box key={item.pid} sx={{
            borderColor:"red",
            borderWidth:"1px",
            width:z(width),
            }}>
        <Link href="/details/1">
            <ImageListItem  >
                <img
                    src={`http://localhost:5000/images/${item.letmeSee}`}
                    srcSet={`${item.img}`}
                    alt={item.title}
                    loading="lazy"
                    style={{
                      backgroundColor:"gray",
                      height:"200px",
                  }}
                />
        </ImageListItem>
        </Link>
        <p style={{backgroundColor:"white",
          maxHeight :"110px",
          width:z(width),
          overflow: "hidden",
          textOverflow:"..."
            }}>{item.pname}
            </p>
        <Stack direction={"row"} gap={2}>
          <p style={{color:"green"}}>{item.price}</p>
          <p style={{color:"red"}}> <del>{item.marketprice}</del></p>
          <Button sx={{
            height:"30px"
          }} variant="contained" startIcon={<ShoppingCart />}
          onClick={()=>{
            handleaddCart(item.title)
          }}
          >
           Add
          </Button>
        </Stack>
        
        </Box>
      ))}
    </ImageList>}
    {
      itemData.length===0 && <div> Our beloved customers, the product we have for know is this please keep in touch we are adding other products ,
        please  enjoy  other products !</div>

    }
     <div style={{
        position:"relative",
        marginTop:"10px",
        backgroundColor:"yellow",
      }}> <Pagination count={nopage} color="primary" page={page} onChange={handleChange} sx={{position:"relative" }}/>
      </div>
      </Box>
      </Box>
  );
}
