import React,{useState,useEffect,useMemo} from 'react';
import NavbarAdmin from './adminNav';
import { Avatar, TextField, Typography } from '@mui/material';
import { Box } from '@mui/system';
import Autocomplete from '@mui/material/Autocomplete';
import axios from 'axios';
const api=axios.create({
  baseURL:"http://localhost:5000/special"
});
const [managerFname,setmanagerFname]=useState('');
const [manageLname,setmanageLname]=useState('');
const [companyName,setCompanyName]=useState('');
const [phoneNo,setphoneNo]=useState('');

const [region,setregion]=useState('');
const [Stream,setStream]=useState('');
const [city,setcity]=useState('');
const [password,setpassword]=useState('');
const handleSumbit= async(e)=>{
  e.preventDefault();
  api.post('/addseller',
    {
      managerFname,manageLname,companyName,
      phoneNo,region,city,
      Stream,password
    },
    {
      withCredentials:true
    }
  ).then(res=>{
    console.log(res.data);
  }).catch(err=>{
    if (err.response) {
      console.log("response error");

  } else if (err.request) {
      // The client never received a response, and the request was never left
      console.log("request error");
  } else {
      // Anything else
      console.log("something went wrong ");
  }
  });


}
const AddSeller = () => {
  const regions = [
    { title: 'Oromia'},
    { title: 'AddisAbaba'},
    { title: 'Amhara'},
    { title: 'Afar' },
    { title: 'Somali' },
    { title: "Sidama" },
    { title: 'BenishangulGumuz'},
    { title: 'Gambella' },
    { title: "SNNP" },
    { title: 'DireDewa'}
]
    return ( 
        <>
          <NavbarAdmin/>
          <Box sx={{
        position:"absolute",
        top:"100px",
        left:"10%",
        width:"80%",
        backgroundColor:"white",

    }}>
      <Typography variant="h4" sx={{
        color:"brown",

      }}> Add Our Seller</Typography>
        <form onSubmit={handleSumbit}>
        <TextField label="Manager FirstName" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} ></TextField> 
        <TextField label="Manager LastName"sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }}  ></TextField> 
        <br></br>
        <br></br>
        <TextField label="CompanyName" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} ></TextField> 
        <TextField label="Phone No" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} ></TextField>
        <br></br>
        <br></br>
        <Autocomplete
        id="Region"
        freeSolo
        options={regions.map((option) => option.title)}
        renderInput={(params) => <TextField {...params} label="Region" />}
      />
        <TextField label="PhoneNumber" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} ></TextField>
        <br></br>
        <br></br>
        <TextField label="City" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} ></TextField>
        <TextField label="Stream" sx={{
            marginLeft:"20px",
            marginTop:"10px",
            marginBottom:"30px"
        }} ></TextField>
        
        <button type="submit">Edit</button>
        
    </form>
    <br/>
    <br/>
    <br/>
        <div> ------</div>
    </Box>
        </>
     );
}
 
export default AddSeller;
