import React,{useEffect,useState} from 'react';
import { useNavigate } from 'react-router-dom';
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import ImageListItemBar from '@mui/material/ImageListItemBar';
import { Box } from '@mui/material';
import ImgMediaCard from './ImageMedia';
import { Stack } from '@mui/system';
import Pagination from '@mui/material/Pagination';
import axios from 'axios';
export default function Feed() {
  const y= (x)=>{
    if(x<600) return 2;
    else  return 4;
  }
  var width = window.innerWidth;
  const api=axios.create({
    baseURL:"http://localhost:5000"
  })
  const [products,setProducts] =useState([]);
  const [wait,setwait]=useState(false)
  useEffect(()=>{
    api.get('/product')
    .then(res=>{
      const a=res.data;
      const newArray = [...products, a];
      // setProducts(newArray);
      setProducts(newArray);
      setwait(true);

      // console.log("a",a);
      console.log("pr",products)
    })
    .catch(err=>{
      console.log(err)
    })
  },[])
  useEffect(()=>{
      // console.log("pr",products)
  },[products])
  return (
    <Box flex={7}  >
    <ImageList 
      cols={y(width)}
    >
      {
       wait&& products.map(value=>{
          // console.log("value",value)
        // console.log("in the map",product[0].pname);
        // <Box key={product.pid} sx={{height:"100px",width:"100px",position:"relative", baackgroundColor:"yellow"}}>hello</Box>
        <ImgMediaCard />
      })
      }
      {
        !wait&& <p> ...... Waiting</p>
      }
      {/* <ImgMediaCard />
      <ImgMediaCard />
      <ImgMediaCard />
      <ImgMediaCard />
      <ImgMediaCard />
      <ImgMediaCard />
      <ImgMediaCard />
      <ImgMediaCard />
      <ImgMediaCard /> */}
    </ImageList>
    <Stack spacing={2}>
      <Pagination count={10} color="primary" />
    </Stack>
    <Box height={100}>

    </Box>
    </Box>
  );
}
