import React,{ useState }  from 'react';
import NavbarSeller from './navbarSeller';
import {MenuItem,Box,Typography, Button } from '@mui/material';
import TextField from '@mui/material/TextField';
import CameraAltIcon from '@mui/icons-material/CameraAlt';
const AddProduct = () => {
  
    return ( 
    <div>
        <NavbarSeller/>
        <Box sx={{
            position:"absolute",
            left:"10%",
            top:"10%",
            width:"80%",
            backgroundColor:"white",

        }}>
            <Typography variant="h4">AddProduct</Typography>
        <form action="http://localhost:5000/sel/upload" method='POST' encType ="multipart/form-data" >
          <br/><br/>
        <TextField fullWidth label="productName" id="fullWidth"  type="text" required name='pname'/>
        <TextField fullWidth label="Price" id="fullWidth" type="number" required name='price'/>
        <label for="inputPic" style={{
          backgroundColor:"blue",
          color:"white",
          width:"120px",
          height:"50px",
          alignItems:"center",
          fontSize:"16px"
        }}>
          <i><CameraAltIcon/> </i>
            Images
        <input type="file" id='inputPic' required multiple name="image" style={{
          display:"none"
        }} /> 
        </label>  
        <br></br>
        <label for="inputVid" style={{
          backgroundColor:"black",
          color:"white",
          width:"120px",
          height:"50px",
          alignItems:"center",
          fontSize:"16px"
        }}>
          <i><CameraAltIcon/> </i>
            video
        <input type="file" id='inputVid'   name="bema" style={{
          display:"none"
        }} /> 
        </label> 
        
        <button type="submit"></button>
      
        </form>
        <hr/>
        <hr/>
        </Box>

    </div>
     );
}

export default AddProduct;
