import React,{ useState,useEffect } from "react";
import {useNavigate} from 'react-router-dom';
import { Avatar, Button, CircularProgress, TextField, Typography } from '@mui/material';
import { Box } from '@mui/system';
import NavbarAdmin from "./adminNav"; 
import axios from 'axios';
import { Edit } from "@mui/icons-material";
const MyProfileA = () => {
    const nav=useNavigate();
    const [editMode,setEditMode]=useState(false);
    const [profiles,setProfiles]=useState([]);
    const [changepassword,setchangepassword]=useState(false);
    const [pp,setpp]=useState('');
    const [np,setnp]=useState('');
    const [cp,setcp]=useState('');
    const [message,setmessage]=useState('');
    const handleSubmit=()=>{
        
    }
    // const [profiles,setProfiles]=useState({})
    const [auth,setAuth]=useState(false);
    const api=axios.create({
        baseURL:"http://localhost:5000/special"
      });
    const handlechangepp=()=>{
        const api=axios.create({
            baseURL:"http://localhost:5000/special"
          });
        api.get('/getprofilea',
      ).then(res=>{
        setAuth(true);
        if(res.status===200)
        {
            let received=[];
            received.push(res.data);
            setProfiles({...received});
            // console.log("received",received)
            // console.log("profiles",profiles)
            // console.log(profiles[0]);
        }
        }).catch(err=>{
            if (err.response) {
                nav('/adlogin') 
                console.log(err);
        } else if (err.request) {
            nav('/adlogin') 

      } else {
          // Anything else
          console.log(err);
          nav('/adlogin') 
        
      }
      });

    }
    const handlechangepassword=()=>{
        const api=axios.create({
            baseURL:"http://localhost:5000/special"
          });
        api.get('/getprofilea',
      ).then(res=>{
        setAuth(true);
        if(res.status===200)
        {
            let received=[];
            received.push(res.data);
            setProfiles({...received});
            // console.log("received",received)
            // console.log("profiles",profiles)
            // console.log(profiles[0]);
        }
        }).catch(err=>{
            if (err.response) {
                nav('/adlogin') 
                console.log(err);
        } else if (err.request) {
            nav('/adlogin') 

      } else {
          // Anything else
          console.log(err);
          nav('/adlogin') 
        
      }
      });

    }
    useEffect((nav)=>{
        const api=axios.create({
            baseURL:"http://localhost:5000/special"
          });
        api.get('/getprofilea',
      ).then(res=>{
        setAuth(true);
        if(res.status===200)
        {
            let received=[];
            received.push(res.data);
            setProfiles({...received});
            // console.log("received",received)
            // console.log("profiles",profiles)
            // console.log(profiles[0]);
        }
        }).catch(err=>{
            if (err.response) {
                nav('/adlogin') 
                console.log(err);
        } else if (err.request) {
            nav('/adlogin') 

      } else {
          // Anything else
          console.log(err);
          nav('/adlogin') 
        
      }
      });
    
      },[])
    return ( 
        <>
    {auth &&
        <>
        <NavbarAdmin/>
        <Box sx={{
        position:"absolute",

        width:"100%",
        height:"150px",
        backgroundColor:"#860C0C"
     }}>
        <Typography variant="h4" sx={{
            position:"absolute",
            left:"15%",
            top:"60px",
            color:"white",
            alignSelf:"center"
        }}> HARENA.com the leading Ecommerce Website !</Typography>
        </Box>
        <Avatar sx={{
            position:"absolute",
            left:"4%",
            width:"100px",
            top:"110px",
            height:"100px"
        }}></Avatar>
    {editMode && <Box sx={{
        position:"absolute",
        top:"30%",
        left:"10%",
        width:"80%",
        backgroundColor:"white",

    }}>
        <Button onClick={()=>{
       setEditMode(false) 
        } } > BACK</Button>
        <form onSumbit={handlechangepp}>
        <TextField label="Manager FirstName" placeholder={profiles[0].aFname} sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} ></TextField> 
        <TextField label="Manager LastName"sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} placeholder={profiles[0].aLname} ></TextField> 
        <br></br>
        <br></br>
        <TextField label="TelegramUsername" placeholder={profiles[0].telUsername} sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} ></TextField> 
        <TextField label="Phone No"  placeholder={profiles[0].phonenumber} sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} ></TextField>


        <br></br>
        <br></br>

        <button type="submit">Edit</button>
        
    </form>
    </Box>
    },
    {!editMode && <Box sx={{
        position:"absolute",
        top:"30%",
        left:"10%",
        width:"80%",
        backgroundColor:"white",

    }}>
        
    <Typography>AdminName     --------------    {profiles[0].aFname+ "  " +profiles[0].aLname }</Typography>
    <br/>
    <br/>
    <Typography>PhoneNumber   --------------    {profiles[0].phonenumber} </Typography>
    <br/>
    <br/>
    <Button onClick={()=>{
       setEditMode(true) 
    } } > <Edit/> Edit</Button>
    <Button onClick={()=>{
       setEditMode(false) 
        } } > BACK</Button>
    </Box>}
    
    {!changepassword && <form onSubmit={handlechangepassword}>
            <p><b>{message}</b></p>
        <TextField required label="oldpasswprd" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} 
        onChange={(e)=>setpp(e.target.value)}
        ></TextField>   
        <TextField required label="newpassword" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} 
        onChange={(e)=>setnp(e.target.value)}
        ></TextField>  
        <TextField required label="confirmpassword" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} 
        onChange={(e)=>setcp(e.target.value)}
        ></TextField>  
        <br/>
    <br/>   
        <Button type='submit' variant='contained' color="primary" >POST</Button>   
    </form>}
    <br/>
    <br/>
    <br/>
        <div> ------</div>
    </>
    },
    { !auth && <CircularProgress/>} 
    </>
    );
}

export default MyProfileA;
