import React,{useState,useEffect} from 'react';
import { styled } from '@mui/material/styles';
import {Box,List,ListItem,ListItemAvatar,ListItemText,IconButton,Typography,Link} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import Navbar from './Navbar';
import axios from 'axios';
const api=axios.create({
  baseURL:"http://localhost:5000/custom"
})
export default function Cart() {
  const [dense, setDense] = React.useState(false);
  const [carts,setcarts]=useState([]);
  useEffect(()=>{
      api.get('/cart',{
        withCredentials:true
      })
      .then(res=>{

        setcarts([...res.data.rows]);
      })
      .catch(err=>{
        console.log(err);
      })
      
  },[])
  return (
    <Box sx={{ flexGrow: 1, width:"100%" }}>
        <Navbar/>
        <Box sx={{position:"relative",marginTop:"20px"}}>
            <List   dense={dense} sx={{
                position: "absolute",
                top: "15%",
                left:" 5%",
                width:"90%",
                mt:"20px"
        }}
        >
            {
          carts.map((cart)=>(  
            <ListItem   key={cart.cid}
            sx={{
                height:"150px",marginBottom:"5px"
            }}
            secondaryAction={
                <IconButton edge="end" aria-label="delete" >
                <DeleteIcon />
                </IconButton>
            }
        >
        <ListItemAvatar>
            <Box 
            sx={{
                height:"150px",
                width:"150px",
                ml:"2px",
                mr:"5px",
                backgroundColor:"greenyellow"
            }} 
            > 
            <Link href={`/details/${cart.Product.pid}`}><img
                    src={`http://localhost:5000/images/${cart.Product.letmeSee}`}
                    alt={cart.Product.pname}
                    loading="lazy"
                    
                    style={{
                      width:"150px",
                      height:"150px"
                    }}
                />
              </Link>
            </Box>
        </ListItemAvatar>
        <ListItemText
            primary={cart.Product.pname}
            secondary={cart.Product.price+" birr"}
            />
        </ListItem> 
          )
        )
      }
            </List>
            </Box>
    </Box>
  );
}
