import Paper from '@mui/material/Paper';
import Button from '@mui/material/Button';
import React,{useState,useEffect,useMemo} from 'react';
import {useNavigate,useParams} from "react-router-dom";
import axios from "axios";
import { Box,TextField } from '@mui/material';
import NavbarAdmin from './adminNav';
const EditOrder = () => {
    const api=axios.create({
        baseURL:"http://localhost:5000/special"
    })
    const orderid=useParams();
    const id=orderid.oid;
    console.log("the order id is ",orderid);
    const handleDelete=()=>{

    }
    const handleDelivered=()=>{

    }
    const handleConfirmed=()=>{

    }
    const [data,setdata]=useState();
    const [found,setfound]=useState(false);
    const nav=useNavigate();
    useEffect(()=>{
        api.get(`/watchorder/${id}`,
    {
      withCredentials:true
    }
  ).then(res=>{
    let bd=res.data;
    if(res.status===200){
        setfound(true);
        console.log(res);
        setdata(res.data);
    }
       // setErrmsg("Successfull login")
    console.log(res);
  }).catch(err=>{
    if (err.response) {
      nav('/adlogin') 
      console.log(err);
  } else if (err.request) {
      // The client never received a response, and the request was never left
      nav('/adlogin') 
      console.log(err);
  } else {
      // Anything else
      // nav('/adlogin') 
        console.log(err);
  }
  });
    },[])
    return ( 
        <>
        <NavbarAdmin/>
        {
            found&&<div sx={{position:"absolute" ,
            top:"100px"}}
            >
            <Box>
                <br></br>
                <br></br>
        <TextField defaultValue={data.Customer.fname+" "+data.Customer.lname}  label="Customer" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} ></TextField> 
        <TextField defaultValue={data.Customer.phone}  label="Customer Phone"sx={{
            marginLeft:"20px",
            marginTop:"10px",
        }}  ></TextField> 
        <br></br>
        <br></br>
        <TextField defaultValue={data.name} label="Product" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} ></TextField> 
        <TextField defaultValue={data.price}  label="Price" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} ></TextField>
        <br></br>
        <br></br>
        
        <TextField defaultValue={data.Seller.managerFname+" "+data.Seller.managerLname} label="Seller Name" sx={{
            marginLeft:"20px",
            marginTop:"10px",
        
        }} ></TextField>
        <TextField defaultValue={"profiles.Stream"} label="Company" sx={{
            marginLeft:"20px",
            marginTop:"10px",
            
        }} ></TextField>
        <br></br>
        <br></br>
        <TextField defaultValue={"profiles.Stream"} label="Phone Number" sx={{
            marginLeft:"20px",
            marginTop:"10px",
           
        }} ></TextField>
        <TextField defaultValue={"profiles.Stream"} label="Location" sx={{
            marginLeft:"20px",
            marginTop:"10px",
            marginBottom:"30px"
        }} ></TextField>
        <br></br>
        <Box sx={{
                backgroundColor:"blue",
                justifyContent:'center',
                alignItems:"center"
        }}>
                <h2 style={{
                    color:"white"
                }}> Admin Can Fill This Area </h2>
        </Box>
        <br></br>
        <br></br>
        <b>Order Description</b>
        <br/>
        <textarea label="Description" style={{
            marginLeft:"20px",
            marginTop:"10px",
            marginBottom:"30px",
            width:"80%",
            columns:5
            
        }} ></textarea>
        <br></br>
        <br></br>
        <TextField defaultValue={"profiles.region"} type="number" label="Amount" sx={{
            marginLeft:"20px",
            marginTop:"10px",
            color:"red"
        }} 
        ></TextField>
        
        <TextField defaultValue={"profiles.city"} type="number"  label="Total Price" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} ></TextField>
        <br></br>
        <br></br>

        <Button sx={{marginLeft:"20px",
            marginTop:"10px",backgroundColor:"blue",color:"white"
            }}> Confirmed</Button> 
        <Button sx={{
            marginLeft:"20px",
            marginTop:"10px",
            backgroundColor:"green",color:"white"
        }}>Delivered
        </Button>
        <Button sx={{marginLeft:"20px",
            marginTop:"10px",backgroundColor:"red",color:"white"
            }}> Delete</Button>
        </Box>
        </div>}
        {
            !found && <div sx={{position:"absolute" ,marginTop:"px"}}>
            <br></br>
            <br></br>
            <h3> ......... Loading </h3>

            </div>
        }

    </>);
}
 
export default EditOrder;
