import React,{ useState }  from 'react';
import NavbarSeller from './navbarSeller';
import { TextField,MenuItem,Select,InputLabel,Box,Typography } from '@mui/material';
import FormControl from '@mui/material/FormControl';
import ReactDOM from "react-dom";
import axios from "axios";
import { Upload, Button } from "antd";
// import { useForm } from "react-hook-form";
import MultiImageInput from "react-multiple-image-input";

//----///

const AddProduct = () => {
  const [category, setCategory] = React.useState('');
  const handleChange = (event) => {
      setCategory(event.target.value);
  };
  const handleError = (e) => console.log(e);
  const [images, setImages] = useState({});
  const handleSumbit=(e)=>{
    e.preventDefault();
    var formdata = new FormData();
    for (let i = 0; i < Object.keys(images).length; i++) {
      var file = Object.values(images)[i];
      console.log(typeof(file))
      formdata.append(`file+${i}`,file)
      
    }
    console.log("data recorded")
    console.log(images);
}

  const crop = {
    unit: "%",
    aspect: 4 / 3,
    width: "100"
  };
    return ( 
    <div>
        <NavbarSeller/>
        <Box sx={{
            position:"absolute",
            left:"10%",
            top:"10%",
            width:"80%",
            backgroundColor:"white",

        }}>
            <Typography variant="h4">AddProduct</Typography>
        <form onSubmit={handleSumbit}>
        <TextField id="outlined-basic" label="name" variant="outlined" />
        <br/>
        <br/>
        <TextField id="outlined-basic" label="price" variant="outlined" />
        <br/>
        <br/>
        <FormControl fullWidth >
        <InputLabel id="demo-simple-select-label">category</InputLabel>
        <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={category}
            label="category"
            onChange={handleChange}
        >
          <MenuItem value="cosmotics">cosmotics</MenuItem>
          <MenuItem value="shoes">shoes</MenuItem>
          <MenuItem value="laptop">laptop</MenuItem>
        </Select>
        <br/>
        <br/>
        </FormControl>
        <MultiImageInput
            images={images}
            setImages={setImages}
            cropConfig={{ crop, ruleOfThirds: true }}
            max={5}
            allowCrop={false}
            theme="light"
            handleError={handleError}
          />
        <input type="file" name="descVideo" id="descVideo" />
          
          <input type="submit" value="POST" />
                
        </form>

        </Box>
    </div>
     );
}
 
export default AddProduct;
