import React from 'react';
import { Link } from 'react-router-dom';
import { useRef,useState,useEffect } from 'react';
import '../css/login.css';
import axios from 'axios';
const api=axios.create({
  baseURL:"http://localhost:5000/"
});
const handleSumbit=async (e)=>{
  e.preventDefault();
  // let res=api.post('/',{
  //     phonenumber:phonenumber,
  //     password:password
  // })
  try{
    const res=await  api.post('/',
    JSON.stringify({phonenumber,password}),
    {
      headers:{'content-Type':"application/json"},
      withCredentials:true
    }
    
    );
    setSuccess(true);

  }catch(err){
      if( !err?.response){
        setErrmsg
      }
  }
  console.log(res);
}
const LoginPage = () => {
  const useref=useRef();
  const errRef=useRef();
  const [password,setPassword]=useState('');
  const [phonenumber,setphonenumber]=useState('');
  const [errMsg,setErrmsg]=useState('');
  const [success,setSuccess]=useState(false);
  useRef(()=>{
      useref.current.focus();
  },[])
  useRef(()=>{
    setErrmsg('');
  },[phonenumber,password])
  return ( 
    <>
    {
       success?( <p>Logged in </p>):(
    <div class="box">
      <form method="POST" action="#">
      <center><img src="https://img.icons8.com/bubbles/100/000000/user.png" alt="User" /></center>
      <h3>Welcome back</h3>
      <p class="tip">Let's login with your account details</p>
      <label>Phonenumber</label>
      <input type="phonenumber" required maxlength="100"
          onChange={(e)=>setphonenumber(e.target.value)}
      />
      <label>Password</label>
      <input type="password" required maxlength="15" minlength="6" 
        onChange={(e)=>setPassword(e.target.value)}
      />
      <div class="btn">
      <button type="submit" onSubmit={()=>{handleSumbit}} >Login</button>
      <a href="/forget" class="ll">Forget password?</a><
        br></br>
      </div>
      <hr/>
      <p class="mode">New user?<a href="/signup">Signup</a></p>
      <p>By processing, you're agree with our <a href="#">Terms and conditions</a> and <a href="#">Privacy policy</a>.</p>
      </form>
    </div>
       )} 
    </>
  );
}
 
export default L
