import React,{useState,useEffect} from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/signup.css';
const SignUp = () => {
    const [fname,setfname]=useState('');
    const [lname,setlname]=useState('');
    const [telusername,settelusername]=useState('');
    const [phonenumber,setphonenumber]=useState('');
    const [region,setregion]=useState('');
    const [city,setcity]=useState('');
    const [password,setpassword]=useState('');
    const [cp,setcp]=useState(''); 
    const [error,seterror]=useState('')
    const handleLogin=(e)=>{
        e.preventDefault();
        if(password!==cp){
            seterror("please fill the same password fields ")
        }
    }
    return ( 
        <div class="box">
            <form method="POST" action="#">
            <center><img src="https://img.icons8.com/bubbles/100/000000/user.png" alt='User'/></center>
            <h3>Welcome</h3>
            <p class="tip">{error}</p>
            <p style={{color:"red",fontWeight:"bold"}}>error user name</p>
            <label>FirstName</label>
            <input type="text" required onClick={(e)=>{
                setfname(e.target.value)
            }} />
            <label>LastName</label>
            <input type="text" required maxLength="20" onClick={(e)=>{
                setlname(e.target.value)
            }}  />
            <label>Telegram Username</label>
            <input type="text" required maxLength="20" onClick={(e)=>{
                settelusername(e.target.value)
            }} />
            <label>PhoneNumber</label>
            <input type="text" required maxLength="13" onClick={(e)=>{
                setphonenumber(e.target.value)
            }} />
            <label>Region</label>
            <input type="region" required maxLength="20" onClick={(e)=>{
                setregion(e.target.value)
            }} />
            <label>City</label>
            <input type="text" required maxLength="20" onClick={(e)=>{
                setcity(e.target.value)
            }} />
            <label>Password</label>
            <input type="password" required maxLength="20" onClick={(e)=>{
                setpassword(e.target.value)
            }}  />
            <label>confirm Password</label>
            <input type="password" required maxLength="20" onClick={(e)=>{
                setcp(e.target.value)
            }}  />
            <div class="btn">
            <button type="submit">Create account</button>
            </div>
            <hr/>
            <p class="mode">Already user?<a href="/">Login</a></p>
            <p>By processing, you're agree with our <a href="#">Terms and conditions</a> and <a href="#">Privacy policy</a>.</p>
            </form>
        </div>
     );
}
 
export default SignUp;
