import React,{useEffect,useState}from "react"; 
import {useNavigate} from 'react-router-dom';
import NavbarSeller from './navbarSeller';
import { styled } from '@mui/material/styles';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import FolderIcon from '@mui/icons-material/Folder';
import DeleteIcon from '@mui/icons-material/Delete';
import IconButton from '@mui/material/IconButton';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import EditIcon from '@mui/icons-material/Edit';
import { Stack,Box, Link } from "@mui/material";
import axios from 'axios';

const MyProduct = () => {
    const nav=useNavigate();
    const [dense, setDense] = React.useState(false);
    const [message,setmessage]=useState('');
    const [edited,setedited]=useState(false);
    let [products,setProducts]=useState([]);
    const api=axios.create({
        baseURL:"http://localhost:5000/sel"
      });
    const handleDelete=(id)=>{
        console.log(id)
        setmessage('......deleting')
        api.post('/deleteproduct',{id},{
            withCredentials:true
        })
        .then(res=>{
            if(res.status===200){
                setedited(true);
                setmessage("")
            }

        })
        .catch(err=>{
            console.log(err)
        })
    }
    useEffect(()=>{
        console.log("useeffect processing ")
        api.get('/myproductt',
            {
            withCredentials:true
            }
        )
        .then(res=>{
            let bd=res.data;
            if(res.status===200)
                setProducts(bd);
            })
        .catch(err=>{
            if (err.response.status===404) {
                nav('/sellerLogin') 
            }
            else if (err.response) {
                setmessage("some thing went wrong")
            } else if (err.request) {
                // The client never received a response, and the request was never left
                console.log(err);
            } else {
                // Anything else
                // nav('/adlogin') 
                console.log(err);
            }
            });
    },[edited])
    return ( 
        <div>
            <NavbarSeller/>
            <p style={{
                position:"absolute",
                left:"8%",
                top:"9%",
                color:"red"
                }} >{message}</p>
            <List dense={dense}
             sx={{
                position:"absolute",
                left:"8%",
                top:"10%",
                width:"90%",
             }}
             >
                <h3>Products</h3>
                {
                    products&& products.map((product) =>
                    <ListItem key={product.pid}
                    secondaryAction={
                        <Stack direction="row" spacing={1} >
                            <Link underline="none" href={`/editproduct/${product.pid}`}>
                            <IconButton edge="end" aria-label="edit"  >
                                <EditIcon color="primary" />
                            </IconButton>
                            </Link>
                            
                            <IconButton edge="end" aria-label="delete" onClick={()=>{handleDelete(product.pid)} }>
                                <DeleteIcon color="secondary" />
                            </IconButton>
                        </Stack>
                    }
                    >
                    <img
                        style={{
                            backgroundColor:"gray",
                            width:"100px",
                            height:"100px",
                        }}
                        src={`http://localhost:5000/images/${product.Pictures[0].id}`}
                        alt={product.pname}
                    />
                    <ListItemText sx={{
                        position:"relative",
                        marginLeft:"5px"
                    }}
                        primary={product.pname}
                        secondary={" price-"+product.price}
                    />
                    </ListItem>,
                    )
                }
               
            </List>
        </div>
     );
}
export default MyProduct;
