import React,{useState,useEffect} from 'react';
import { styled } from '@mui/material/styles';
import {Box,Button,List,ListItem,ListItemAvatar,ListItemText,IconButton,Typography,Link} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import Navbar from './Navbar';
import axios from 'axios';
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import {ShoppingCart} from '@mui/icons-material';
import { Stack } from '@mui/system';
const api=axios.create({
  baseURL:"http://localhost:5000/custom"
})
const z= (x)=>{
  if(x<600) return "180px";
  else  return "250px";
}
const y= (x)=>{
  if(x<600) return 2;
  else if (x<1000) return 3;
  else  return 5;
}
var width = window.innerWidth;
export default function Cart() {
  const [dense, setDense] = React.useState(false);
  const [carts,setcarts]=useState([]);
  const [loaddelete,selloadelete]=useState(false);
  const handleDeleteCart=(cid)=>{
    console.log("deleting cart cid ",cid);
    api.post('/delcart',{cid},{
      withCredentials:true
    }).then(res=>{
      if(res.status==200){
          selloadelete(!loaddelete);
      }
    })
  }

  useEffect(()=>{
      api.get('/cart',{
        withCredentials:true
      })
      .then(res=>{
        if(res.status==200){
          console.log("res",res);
          setcarts([...res.data.rows]);
        }
      })
      .catch(err=>{
        console.log(err);
      })
      
  },[loaddelete])
  return (
    <Box sx={{ flexGrow: 1, width:"100%" }}>
        <Navbar/>
        <Box sx={{position:"relative",marginTop:"20px"}}>
            <List   dense={dense} sx={{
                position: "absolute",
                top: "15%",
                left:" 5%",
                width:"90%",
                mt:"20px"
        }}
        >
            </List>
            {carts.length>0 && <ImageList cols={y(width)}  sx={{ 
        position:"relative", 
    }}>
        {carts.map((cart) => (
          <Box key={cart.cid} sx={{
            borderColor:"red",
            borderWidth:"1px",
            width:z(width),
            }}>
        <Link href={`/details/${cart.Product.pid}`}>
            <ImageListItem  >
                <img
                    src={`http://localhost:5000/images/${cart.Product.letmeSee}`}
                    alt={cart.Product.pname}
                    loading="lazy"
                    style={{
                      backgroundColor:"gray",
                      height:"200px",
                  }}
                />
        </ImageListItem>
        </Link>
        <p style={{backgroundColor:"white",
          maxHeight :"110px",
          width:z(width),
          overflow: "hidden",
          textOverflow:"..."
            }}>{cart.Product.pname}
            </p>
        <Stack direction={"row"} gap={2}>
          <p style={{color:"green"}}>{cart.Product.price}</p>
          <p style={{color:"red"}}> <del>{cart.Product.marketprice}</del></p>
          <Button sx={{
            height:"30px"
          }} variant="contained" startIcon={<ShoppingCart />}
          onClick={()=>{
            
          }}
          >
          Add
          </Button>
        </Stack>
        
        </Box>
      ))}
    </ImageList>}
            </Box>
    </Box>
  );
}
