import { Component } from "react";
import React from 'react';
import { useState,useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Box, Stack } from "@mui/system";
import {CircularProgress, Typography} from "@mui/material";
import MoneyIcon from '@mui/icons-material/Money';
import { Button, ButtonGroup,Link } from "@mui/material";
import '../css/details.css';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import SendIcon from '@mui/icons-material/Send';
import Navbar from './Navbar';
import axios from "axios";
import { LocationOnSharp, Star } from "@mui/icons-material";

const Details = () => {
    const width=window.width;
    const pwidth=()=>{
        if(width<600){
            return `${width}`;
        }
        else{
            return "700px";
        }
    }
    const poid=useParams();
    const nav=useNavigate();
    let pid=poid.pid;
    const [open, setOpen] = useState(false);
    const handleClose = () => {
    setOpen(false);
    };
    const api=axios.create({
        baseURL:"http://localhost:5000/details"
    })
    const api1=axios.create({
        baseURL:"http://localhost:5000/custom"
    })
    // const id=useParams('id');
    const [pictures,setpictures]=useState([]);
    const [product,setProduct]=useState({});
    const [preview,setPreview]=useState(0);
    const [ordering,setordering]=useState(false);
    const handleOrder=()=>{
        setordering(true);
        api1.post("/order",{
            pid
        },
        {
            withCredentials:true
        }).then(res=>{
            if(res.status===200){
                setOpen(true);
                setordering(false);
            }
        })
        .catch(err=>{
            if(err.response.status===403){
                nav('/')
            }
        })

    }
    useEffect(()=>{
        api.get(`/${pid}`)
        .then(async res=>{
            if(res.status===200){
                let bd=res.data;
                let get={
                    pid:bd.pid,
                    CategoryCid:bd.CategoryCid,
                    SellerSid:bd.SellerSid,
                    description:bd.description,
                    marketprice:bd.marketprice,
                    price:bd.price,
                    pname:bd.pname
                }
                setProduct(get);
                console.log("the product is ",product);
                setpictures([...bd.Pictures]);
                setPreview(bd.Pictures[0].id)
                
            }
            })
            .catch(err=>{
            console.log(err)
            })
    },[])
    return (
        <div>  
            <Navbar/>
            <h3> </h3>
            <Stack direction={"row"} spacing={50}>
                <Box>
                <div className="details" >
                    <Stack direction={"column"}>
                    <div className="bigPicture" style={{
                                position:"absolute",
                                backgroundColor:preview
                            }}>
                            <img
                            style={{
                                height: "380px",
                                width: "350px"
                            }}
                            src={`http://localhost:5000/images/${preview}`}
                            alt={"a"}
                        />
                        </div>
                        <Box  className="photo-list" sx={{
                            position:"absolute",
                            left:"50px",
                            top:"450px",
                            left:"50px"
                            
                        }}>
                    {
                        pictures.map((item)=>(
                        <div key={item.id}
                        onClick={()=>{
                            setPreview(item.id)
                        }}
                        className="details"
                        style={{
                            height:"60px",
                            width:"60px",
                            position:"relative",
                            marginTop: "10px",
                            display: "inline-block",
                            marginLeft: "5px"
                        }}
                                            >
                        <img
                            height="60px"
                            width="60px"
                            src={`http://localhost:5000/images/${item.id}`}
                            alt={"a"}
                        />
                        </div>
