import React,{ useState }  from 'react';
import NavbarSeller from './navbarSeller';
import { TextField,MenuItem,Select,InputLabel,Box,Typography } from '@mui/material';
import FormControl from '@mui/material/FormControl';
import ReactDOM from "react-dom";
import axios from "axios";
import { useForm } from "react-hook-form";
import MultiImageInput from "react-multiple-image-input";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import { makeStyles } from "@material-ui/core/styles";
import ArrowBackIcon from "@material-ui/icons/ArrowBack";

//----///
const useStyles = makeStyles((theme) => ({
    root: {
      margin: 0,
      width: "100%",
      height: "inherit"
    },
    textarea: {
      width: "99%",
      borderColor: "#aaa"
    },
    form: {
      margin: 5
    },
    button: {
      width: "100%",
      backgroundColor: " #30679f",
      border: "none",
      borderRadius: 5,
      height: 40,
      color: "white",
      fontWeight: "bold"
    },
    header: {
      display: "flex",
      marginBottom: 5,
      alignItems: "center",
      justifyContent: "space-between",
      padding: " 0px 5px",
      height: 40,
      backgroundColor: " #30679f",
      color: "white",
      fontWeight: "bold",
      borderRadius: 5
    },
    headerPost: {
      border: "none",
      borderLeft: "solid",
      background: "none",
      color: "white",
      fontWeight: "bold",
      paddingLeft: 5
    },
    headerCreatePost: {
      display: "flex",
      alignItems: "center"
    }
  }));
  
const AddProduct = () => {
    const [category, setCategory] = React.useState('');
    const handleChange = (event) => {
        setCategory(event.target.value);
    };
    const handleSumbit=()=>{
        console.log("hello sumbit ");
    }
    
  const handleError = (e) => console.log(e);
  const [images, setImages] = useState({});
  const crop = {
    unit: "%",
    aspect: 4 / 3,
    width: "100"
  };
    return ( 
    <div>
        <NavbarSeller/>
        <Box sx={{
            position:"absolute",
            left:"10%",
            top:"10%",
            width:"80%",
            backgroundColor:"white",

        }}>
            <Typography variant="h4">AddProduct</Typography>
        <form>
        <TextField id="outlined-basic" label="Outlined" variant="outlined" />
        <br/>
        <br/>
        <TextField id="outlined-basic" label="Outlined" variant="outlined" />
        <br/>
        <br/>
        <FormControl fullWidth >
        <InputLabel id="demo-simple-select-label">category</InputLabel>
        <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={category}
            label="category"
            onChange={handleChange}
        >
          <MenuItem value="cosmotics">cosmotics</MenuItem>
          <MenuItem value="shoes">shoes</MenuItem>
          <MenuItem value="laptop">laptop</MenuItem>
        </Select>
        <br/>
        <br/>
        </FormControl>
        <MultiImageInput
            images={images}
            setImages={setImages}
            cropConfig={{ crop, ruleOfThirds: true }}
            max={5}
            allowCrop={false}
            theme="light"
            handleError={handleError}
          />
        </form>
        </Box>
    </div>
     );
}
 
export default AddProduct;
