import React,{ useState,useEffect } from "react";
import {useNavigate} from 'react-router-dom';
import { Avatar, Button, CircularProgress, TextField, Typography } from '@mui/material';
import { Box } from '@mui/system';
import NavbarAdmin from "./adminNav"; 
import axios from 'axios';
import { Edit } from "@mui/icons-material";
const MyProfileA = () => {
    const nav=useNavigate();
    const [editMode,setEditMode]=useState(false);
    const [normal,setnormal]=useState(true);
    const [profiles,setProfiles]=useState([]);
    const [changepassword,setchangepassword]=useState(false);
    const [pp,setpp]=useState('');
    const [np,setnp]=useState('');
    const [cp,setcp]=useState('');
    //---//
    const [adfname,setadfname]=useState('');
    const [adlname,setadlname]=useState('');
    const [adtelphone,setadtelphone]=useState('');
    const [telUsername,settelUsername]=useState('');
    const [message,setmessage]=useState('');
    const [auth,setAuth]=useState(false);
    const api=axios.create({
        baseURL:"http://localhost:5000/special"
      });


  
    const handlechangepp=(e)=>{
        e.preventDefault();
        if(adfname===''){
            adfname=profiles[0].aFname
        } 
        if(adlname===''){
            adlname=profiles[0].phonenumber;
        }
        if(adtelphone===''){
            adtelphone=profiles[0].telUsername;
        }
        api.post('/changepp',{
            adfname,adlname,adtelphone
        }
      ).then(res=>{
        if(res.status===200)
        {
            setmessage("successfully updated profile")
        }
        }).catch(err=>{
            if (err.response) {
                setmessage("something went wrong");
            } else if (err.request) {
                setmessage("something went wrong");
            } else {
                // Anything else
            
                nav('/adlogin') 
                
            }
      });

    }
    const handlechangepassword=(e)=>{
        e.preventDefault();
        if(np!==cp){
            setmessage("password should be the same ")
            return;
        }
        api.post('/changepassword',{

        }
      ).then(res=>{
        if(res.status===200)
        {
           setmessage("successfully updated")
        }
        }).catch(err=>{
            if (err.response) {
                setmessage("wrong password");
                console.log(err);
        } else if (err.request) {
                setmessage("you are currently offline")

      } else {
          // Anything else
          console.log(err);
          nav('/adlogin') 
        
      }
      });

    }
    useEffect((nav)=>{
        const api=axios.create({
            baseURL:"http://localhost:5000/special"
          });
        api.get('/getprofilea',
      ).then(res=>{
        setAuth(true);
        if(res.status===200)
        {
            let received=[];
            received.push(res.data);
            setProfiles({...received});
            // console.log("received",received)
            // console.log("profiles",profiles)
            // console.log(profiles[0]);
        }
        }).catch(err=>{
            if (err.response) {
                nav('/adlogin') 
                console.log(err);
        } else if (err.request) {
            nav('/adlogin') 

      } else {
          // Anything else
          console.log(err);
          nav('/adlogin') 
        
      }
      });
    
      },[normal])
    return ( 
        <>
    {auth &&
        <>
        <NavbarAdmin/>
        <Box sx={{
        position:"absolute",

        width:"100%",
        height:"150px",
        backgroundColor:"#860C0C"
     }}>
        <Typography variant="h4" sx={{
            position:"absolute",
            left:"15%",
            top:"60px",
            color:"white",
            alignSelf:"center"
        }}> HARENA.com the leading Ecommerce Website !</Typography>
        </Box>
        <Avatar sx={{
            position:"absolute",
            left:"4%",
            width:"100px",
            top:"110px",
            height:"100px"
        }}></Avatar>
     <Button variant="contained" onClick={()=>{
       setEditMode(true) 
       setnormal(false)
       setchangepassword(false);
    } }  sx={{
        position:"absolute",
        top:"210px",
        left:"0%",
        width:"100px"
    }}> <Edit/> Edit</Button>
    <Button variant="contained" onClick={()=>{
       setchangepassword(true) 
       setnormal(false)
       setEditMode(false)
    } }  sx={{
        position:"absolute",
        top:"210px",
        left:"31%",
        ml:"5px",
        
    }}> <Edit/> Password</Button>
    <Button variant="contained" onClick={()=>{
       setEditMode(false)
       setnormal(true)
       setchangepassword(false)
     } } 
      
       sx={{
            position:"absolute",
            top:"210px",
            left:"70%",
            ml:"5px",
            width:"100px",
        }}> BACK</Button>
  
    {editMode && <Box sx={{
        position:"absolute",
        top:"36%",
        left:"10%",
        width:"80%",
        backgroundColor:"white",

    }}>
        <form onSumbit={handlechangepp}>
        <h6><b>{message}</b></h6>
        <TextField required label="Manager FirstName" placeholder={profiles[0].aFname} sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }}
        onChange={(e)=>setadfname(e.target.value)}
        ></TextField> 
        <TextField required label="Manager LastName"sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} placeholder={profiles[0].aLname} 
        onChange={(e)=>setadlname(e.target.value)}
        ></TextField> 
        <br></br>
        <br></br>
        <TextField required label="TelegramUsername" placeholder={profiles[0].telUsername} sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} 
        onChange={(e)=>settelUsername(e.target.value)}></TextField> 
        <TextField required label="Phone No"  placeholder={profiles[0].phonenumber} sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} 
        onChange={(e)=>setadtelphone(e.target.value)}></TextField>


        <br></br>
        <br></br>

        <button type="submit">Edit</button>
        
    </form>
    </Box>
    },
    {normal && <Box sx={{
        position:"absolute",
        top:"36%",
        left:"10%",
        width:"80%",
        backgroundColor:"white",

    }}>
        
    <Typography>AdminName     --------------    {profiles[0].aFname+ "  " +profiles[0].aLname }</Typography>
    <br/>
    <br/>
    <Typography>PhoneNumber   --------------    {profiles[0].phonenumber} </Typography>
    <br/>
    <br/>
    <Typography>Tel username   --------------    {profiles[0].telUsername} </Typography>
    </Box>
    }
    {changepassword && <form onSubmit={handlechangepassword}
      
      style={{
        position:"absolute",
        top:"36%",
        left:"10%",
        width:"80%",
        backgroundColor:"white",

    }}
     >
            <h6><b>{message}</b></h6>
        <TextField required label="oldpasswprd" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} 
        onChange={(e)=>setpp(e.target.value)}
        ></TextField>   
        <TextField required label="newpassword" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} 
        onChange={(e)=>setnp(e.target.value)}
        ></TextField>  
        <TextField required label="confirmpassword" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} 
        onChange={(e)=>setcp(e.target.value)}
        ></TextField>  
        <br/>
    <br/>   
        <Button type='submit' variant='contained' color="primary" >POST</Button>   
    </form>}
    <br/>
    <br/>
    <br/>
        <div> ------</div>
    </>
    },
    { !auth && <CircularProgress/>} 
    </>
   
    );
}

export default MyProfileA;
