import React,{ useState }  from 'react';
import NavbarSeller from './navbarSeller';
import {MenuItem,Box,Typography } from '@mui/material';
import TextField from '@mui/material/TextField';

const AddProduct = () => {
  
    return ( 
    <div>
        <NavbarSeller/>
        <Box sx={{
            position:"absolute",
            left:"10%",
            top:"10%",
            width:"80%",
            backgroundColor:"white",

        }}>
            <Typography variant="h4">AddProduct</Typography>
        <form action="http://localhost:5000/sel/upload" method='POST' encType ="multipart/form-data" >
          <br/><br/>
        <TextField fullWidth label="productName" id="fullWidth"  type="text" required name='pname'/>
        <TextField fullWidth label="Price" id="fullWidth" type="number" required name='price'/>
        <label>
        <input type="file" required multiple name="image" style={{
          display:"none"
        }} />
        AddPhotos
        </label>
          AddPhotos
        <input type="file"   name="bema">
        </input>
        <input type="submit" />
        </form>
        <hr/>
        <hr/>
        </Box>

    </div>
     );
}

export default AddProduct;
