import * as React from 'react';
import ListSubheader from '@mui/material/ListSubheader';
import List from '@mui/material/List';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Collapse from '@mui/material/Collapse';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import DraftsIcon from '@mui/icons-material/Drafts';
import SendIcon from '@mui/icons-material/Send';
import ExpandLess from '@mui/icons-material/ExpandLess';
import ExpandMore from '@mui/icons-material/ExpandMore';
import StarBorder from '@mui/icons-material/StarBorder';
import axios from 'axios';
import { useState,useEffect } from 'react';
export default function NestedList({mobileOpen,setMobileOpen}) {
  const [open, setOpen] = React.useState(true);
  const [subopen, setsubopen] = React.useState(true);
  const [broadopen, setbroadopen] = React.useState(true);
  const [categories,setCategories]=useState([]);
  const handleClick = () => {
    setOpen(!open);
  }; 
  const handleSubCat=()=>{
    setsubopen(!subopen);
  }
  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
};
  const api= axios.create({
    baseURL:"http://localhost:5000/custom"
  })
  useEffect(()=>{
  api.get('/categories')
    .then(res=>{
        console.log(res)
        let data=res.data;
        console.log(data);
        setCategories(data);
        console.log("categories",categories)
    })
    .catch(err=>{
      console.log(err);
    })
  },[])
  function sublist(broad) {
    return (
      <List>
        {broad.map(subcat => {
            <ListItemButton key={subcat.cid} onClick={handleDrawerToggle}>
              <ListItemText primary="Sent mail" />
          </ListItemButton>
          })}
      </List>
    )
  }
  return (
    <>
    {
      broadopen &&<List
      sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}
      component="nav"
      aria-labelledby="nested-list-subheader"
      // subheader={
      //   <ListSubheader component="div" id="nested-list-subheader">
      //     Nested List Items
      //   </ListSubheader>
      // }
    >
      {
        categories.map((broad)=>(
          <ListItemButton key={broad.pid} onClick={()=>{
            sublist(broad)
          }}>
            <ListItemText primary="Inbox" />
              <ExpandMore />
          </ListItemButton>
        
        ))
      }
      <ListItemButton onClick={handleDrawerToggle}>
        <ListItemText primary="Sent mail" />
      </ListItemButton>

    </List>}
    </>
  );
}
