import * as React from 'react';
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import ImageListItemBar from '@mui/material/ImageListItemBar';
import ListSubheader from '@mui/material/ListSubheader';
import IconButton from '@mui/material/IconButton';
import InfoIcon from '@mui/icons-material/Info';
import { Box, Typography } from '@mui/material';
import { useState,useEffect } from 'react';
import { useCallback } from 'react';
const itemData = [    
    {
      img: 'https://images.unsplash.com/photo-1551782450-a2132b4ba21d',
      title: 'Burger',
      author: '@rollelflex_graphy726',
    },
    {
      img: 'https://images.unsplash.com/photo-1522770179533-24471fcdba45',
      title: 'Camera',
      author: '@helloimnik',
    },
    {
      img: 'https://images.unsplash.com/photo-1444418776041-9c7e33cc5a9c',
      title: 'Coffee',
      author: '@nolanissac',
      cols: 2,
    },
    {
      img: 'https://images.unsplash.com/photo-1533827432537-70133748f5c8',
      title: 'Hats',
      author: '@hjrc33',
      cols: 2,
    },
    {
      img: 'https://images.unsplash.com/photo-1558642452-9d2a7deb7f62',
      title: 'Honey',
      author: '@arwinneil',
      rows: 2,
      cols: 2,
      featured: true,
    },
    {
      img: 'https://images.unsplash.com/photo-1516802273409-68526ee1bdd6',
      title: 'Basketball',
      author: '@tjdragotta',
    },
    {
      img: 'https://images.unsplash.com/photo-1518756131217-31eb79b20e8f',
      title: 'Fern',
      author: '@katie_wasserman',
    },
    {
      img: 'https://images.unsplash.com/photo-1597645587822-e99fa5d45d25',
      title: 'Mushrooms',
      author: '@silverdalex',
      rows: 2,
      cols: 2,
    },
    {
      img: 'https://images.unsplash.com/photo-1567306301408-9b74779a11af',
      title: 'Tomato basil',
      author: '@shelleypauls',
    },
    {
      img: 'https://images.unsplash.com/photo-1471357674240-e1a485acb3e1',
      title: 'Sea star',
      author: '@peterlaster',
    },
    {
      img: 'https://images.unsplash.com/photo-1551963831-b3b1ca40c98e',
      title: 'Breakfast',
      author: '@bkristastucchio',
      rows: 2,
      cols: 2,
      featured: true,
    },
    {
      img: 'https://images.unsplash.com/photo-1589118949245-7d38baf380d6',
      title: 'Bike',
      author: '@southside_customs',
      cols: 2,
    },
  ];

export default function Categories() {
  let [a,seta]=useState('') ; 
  const advertImages=[
    'https://plus.unsplash.com/premium_photo-1666497635505-fbe5ef5062f1?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1074&q=80',
    'https://images.unsplash.com/photo-1589118949245-7d38baf380d6',
    'https://images.unsplash.com/photo-1471357674240-e1a485acb3e1'
  ]
  let length=3;
  let [index,setIndex]=useState(0);
    useEffect(()=>{
      const interval=setInterval(() => {
        if (index==3) {
          index=0;
          seta(advertImages[index])
        }
        else{
          seta(advertImages[index]);
        }
        setIndex(index+1);
      }, 15000);
      return ()=>clearInterval(interval);
    },[a])
    return (
      <div>
    <Box sx={{
        position :"absolute",
        height:"300px",
        width:"100%",
        backgroundImage:`url(${a})`,
        backgroundRepeat: 'no-repeat',
        backgroundRepeat:"repeat-x"
    }}>
    </Box>
    <ImageList gap={10} sx={{ position:"absolute", 
    top:"350px" , zIndex:"2", width:"80%", background:"grey",
    left:"10%",
    }}>
        <ImageListItem key="Subheader" cols={2}>
    </ImageListItem>
        {itemData.map((item) => (
          <Box>
            <Typography sx={{backgroundColor:"white",
            height:"50px"
            }}>{item.title}</Typography>
            <ImageListItem key={item.img} >
                <img
                    src={`${item.img}?w=248&fit=crop&auto=format`}
                    srcSet={`${item.img}?w=248&fit=crop&auto=format&dpr=2 2x`}
                    alt={item.title}
                    loading="lazy"
                />
            <ImageListItemBar
            title={item.title}
            subtitle={item.author}
            actionIcon={
                <IconButton
                    sx={{ color: 'rgba(255, 255, 255, 0.54)' }}
                    aria-label={`info about ${item.title}`}
                >
                <InfoIcon />
                </IconButton>
            }
        />
        </ImageListItem>
        </Box>
      ))}
    </ImageList>
    <Box > HELLo
          </Box>
    </div>
    );
        }
