import Paper from '@mui/material/Paper';
import Button from '@mui/material/Button';
import React,{useState,useEffect,useMemo} from 'react';
import {useNavigate,useParams} from "react-router-dom";
import axios from "axios";
import { Box,TextField } from '@mui/material';
import NavbarAdmin from './adminNav';
const EditOrder = () => {
    const api=axios.create({
        baseURL:"http://localhost:5000/special"
    })
    const orderid=useParams();
    const oid=orderid.oid;
    // console.log("the order id is ",orderid);
    const [amount,setamount]=useState();
    const [odescription,setodescription]=useState();
    const [totalPrice,settotalPrice]=useState();

    const handleDelete=()=>{
        api.post('/editorder',
        {oid},
        {
            withCredentials:true
          }
    ).then(response=>{
        if(response.status===200){
            console.log(response);
            nav(`/editorder`)
        }
    }).catch(err=>{
        console.log("The error is ",err);

    })
    }
    const handleDelivered=()=>{
        api.post('/editorder',
        {delivered:true},
        {
            withCredentials:true
          }
    ).then(response=>{
        if(response.status===200){
            nav(`/editorder`)
        }
    }).catch(err=>{
        console.log("The error is ",err);

    })
    }
    const handleConfirmed=()=>{
        console.log("confirming.. ")
        api.post('/editorder',
            {amount,odescription,totalPrice},
            {
                withCredentials:true
              }
        ).then(response=>{
            if(response.status===200){
                nav(`/editorder/${oid}`)
            }
        }).catch(err=>{
            console.log("The error is ",err);

        })
    }
    const [data,setdata]=useState();
    const [found,setfound]=useState(false);
    const nav=useNavigate();
    useEffect(()=>{
        api.get(`/watchorder/${oid}`,
    {
      withCredentials:true
    }
  ).then(res=>{
    let bd=res.data;
    if(res.status===200){
        setfound(true);
        console.log(res);
        setdata(res.data);
    }
       // setErrmsg("Successfull login")
    console.log(res);
  }).catch(err=>{
    if (err.response) {
      nav('/adlogin') 
      console.log(err);
  } else if (err.request) {
      // The client never received a response, and the request was never left
      nav('/adlogin') 
      console.log(err);
  } else {
      // Anything else
      // nav('/adlogin') 
        console.log(err);
  }
  });
    },[])
    return ( 
        <>
        <NavbarAdmin/>
        {
            found&&<div sx={{position:"absolute" ,
            top:"100px"}}
            >
            <Box>
                <br></br>
                <br></br>
        <TextField defaultValue={data.Customer.fname+" "+data.Customer.lname}  label="Customer" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} ></TextField> 
        <TextField defaultValue={data.Customer.phone}  label="Customer Phone"sx={{
            marginLeft:"20px",
            marginTop:"10px",
        }}  ></TextField> 
        <br></br>
        <br></br>
        <TextField defaultValue={data.Product.pname} label="Product" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} ></TextField> 
        <TextField defaultValue={data.Product.price}  label="Price" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} ></TextField>
        <br></br>
        <br></br>
    
        <TextField defaultValue={data.Seller.managerFname+" "+data.Seller.manageLname} label="Seller Name" sx={{
            marginLeft:"20px",
            marginTop:"10px",
        
        }} ></TextField>
        <TextField defaultValue={data.Seller.companyName} label="Company" sx={{
            marginLeft:"20px",
            marginTop:"10px",
            
        }} ></TextField>
        <br></br>
        <br></br>
        <TextField defaultValue={data.Seller.phoneNo} label="Phone Number" sx={{
            marginLeft:"20px",
            marginTop:"10px",
           
        }} ></TextField>
        <TextField defaultValue={data.Seller.city} label="Location" sx={{
            marginLeft:"20px",
            marginTop:"10px",
            marginBottom:"30px"
        }} ></TextField>
        <br></br>
        <br></br>
        <TextField defaultValue={data.Seller.subcity} label="Subcity" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} ></TextField> 
        <TextField defaultValue={data.Seller.slocation}  label="slocation" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }} ></TextField>
        <br></br>
        <br></br>
        <Box sx={{
                backgroundColor:"lightblue",
                justifyContent:'center',
                alignItems:"center"
        }}>
                <h2 style={{
                    color:"white"
                }}> Admin Can Fill This Area </h2>
        </Box>
        <br></br>
        <br></br>
        <b>Order Description</b>
        <br/>
        <textarea label="Description" placeholder={data.odescription} style={{
            marginLeft:"20px",
            marginTop:"10px",
            marginBottom:"30px",
            width:"80%",
            columns:5,
            paddinpxg:"4px"
        }} onChange ={(e)=>setodescription(e.target.value)} ></textarea>
        <br></br>
        <br></br>
        <TextField defaultValue={data.amount} type="number" label="Amount" sx={{
            marginLeft:"20px",
            marginTop:"10px",
            color:"red"
        }} 
        onChange={(e)=>{setamount(e.target.value)}}  ></TextField>
        
        <TextField defaultValue={data.totalPrice} type="number"  label="Total Price" sx={{
            marginLeft:"20px",
            marginTop:"10px"
        }}
        onChange={(e)=>{settotalPrice(e.target.value)}}  ></TextField>
        <br></br>
        <br></br>

        <Button sx={{marginLeft:"20px",
            marginTop:"10px",backgroundColor:"blue",color:"white"
            }} onClick={handleConfirmed}> Confirm</Button> 
        <Button sx={{
            marginLeft:"20px",
            marginTop:"10px",
            backgroundColor:"green",color:"white"
        }}onClick={handleDelivered}>Delivered
        </Button>
        <Button sx={{marginLeft:"20px",
            marginTop:"10px",backgroundColor:"red",color:"white"
            }} onClick={handleDelete}> Delete</Button>
        </Box>
        </div>}
        {
            !found && <div sx={{position:"absolute" ,marginTop:"px"}}>
            <br></br>
            <br></br>
            <h3> ......... Loading </h3>

            </div>
        }

    </>);
}
 
export default EditOrder;
