import React,{ useState }  from 'react';
import NavbarSeller from './navbarSeller';
import { TextField,MenuItem,Select,InputLabel,Box,Typography } from '@mui/material';
import FormControl from '@mui/material/FormControl';
import axios from "axios";
// import { useForm } from "react-hook-form";
import MultiImageInput from "react-multiple-image-input";
//----///
const AddProduct = () => {
  const [image, setImage] = useState({ preview: '', data: '' })
  const [status, setStatus] = useState('')
  const [category, setCategory] = React.useState('');
  const handleChange = (event) => {
      setCategory(event.target.value);
  };
  const handleFileChange = (e) => {
    const img = {
      preview: URL.createObjectURL(e.target.files[0]),
      data: e.target.files[0],
    }
    console.log("image",image.data)
    setImage(img)
  }
  const handleSumbit=(e)=>{
    e.preventDefault();

  }
  const [images, setImages] = useState();
  const crop = {
    unit: "%",
    aspect: 4 / 3,
    width: "100"
  };
    return ( 
    <div>
        <NavbarSeller/>
        <Box sx={{
            position:"absolute",
            left:"10%",
            top:"10%",
            width:"80%",
            backgroundColor:"white",

        }}>
            <Typography variant="h4">AddProduct</Typography>
        <form action="http://localhost:5000/sel/upload" method='POST' encType ="multipart/form-data" >
        <input type="file" required multiple name="image" />
        <input type="file"   name="bema" />
        <input type="text" name="name" />
        <input type="number" name="price"/>
        <input type="submit" />
        </form>
        <hr/>
        <hr/>
        <form onSubmit={handleSumbit} >
          <MultiImageInput
            max={3}
            images={setImages}
            setImages={setImages}
            cropConfig={{ crop }}
          />
        </form>

        </Box>

    </div>
     );
}

export default AddProduct;
