import React,{ useState,useEffect }  from 'react';
import NavbarSeller from './navbarSeller';
import {MenuItem,Box,Typography, Button } from '@mui/material';
import TextField from '@mui/material/TextField';
import CameraAltIcon from '@mui/icons-material/CameraAlt';
const AddProduct = () => {
  const [selectedFile, setSelectedFile] = useState()
  const [preview, setPreview] = useState()

  // create a preview as a side effect, whenever selected file is changed
  useEffect(() => {
      if (!selectedFile) {
          setPreview(undefined)
          return
      }

      const objectUrl = URL.createObjectURL(selectedFile)
      setPreview(objectUrl)

      // free memory when ever this component is unmounted
      return () => URL.revokeObjectURL(objectUrl)
  }, [selectedFile])

  const onSelectFile = e => {
      if (!e.target.files || e.target.files.length === 0) {
          setSelectedFile(undefined)
          return
      }

      // I've kept this example simple by using the first image instead of multiple
      setSelectedFile(e.target.files[0])
  }

    return ( 
    <div>
        <NavbarSeller/>
        <Box 
        sx={{
            position:"absolute",
            left:"10%",
            top:"10%",
            width:"80%",
            backgroundColor:"white",

        }}>
        <Typography variant="h4">AddProduct</Typography>
        <form action="http://localhost:5000/sel/upload" method='POST' encType ="multipart/form-data" >
          <br/><br/>
        <TextField  label="productName" id="fullWidth"  type="text" required name='pname'/>
        <br/><br/>
        <TextField  label="Price" id="fullWidth" type="number" required name='price'/>
        <br/><br/>
        <label for="inputPic" style={{
          backgroundColor:"blue",
          color:"white",
          width:"120px",
          height:"50px",
          alignItems:"center",
          fontSize:"16px"
        }}>
          <i><CameraAltIcon/> </i>
            Images
        <input type="file" id='inputPic' onChange={onSelectFile} required multiple name="image" style={{
          display:"none"
        }} /> 
        </label>  
        <br></br>
        <label for="inputVid" style={{
          backgroundColor:"black",
          color:"white",
          width:"120px",
          height:"50px",
          alignItems:"center",
          fontSize:"16px"
        }}>
          <i><CameraAltIcon/> </i>
            video
        <input type="file" id='inputVid'   name="bema" style={{
          display:"none"
        }} /> 
        </label> 
        {selectedFile &&  <img src={preview} /> }
        <button type="submit">POST</button>
      
        </form>
       
        </Box>

    </div>
     );
}

export default AddProduct;
