import { Avatar, TextField, Typography,Button } from '@mui/material';
import { Edit } from "@mui/icons-material";
import { Box } from '@mui/system';
import NavbarSeller from './navbarSeller';
import Autocomplete from '@mui/material/Autocomplete';
import React,{ useState,useEffect } from 'react';
import {useNavigate} from 'react-router-dom'
const MyProfile = () => {
                
    const regions = [
        { title: 'Oromia'},
        { title: 'AddisAbaba'},
        { title: 'Amhara'},
        { title: 'Afar' },
        { title: 'Somali' },
        { title: "Sidama" },
        { title: 'BenishangulGumuz'},
        { title: 'Gambella' },
        { title: "SNNP" },
        { title: 'DireDewa'}
    ]
    const nav=useNavigate();
    const [editMode,setEditMode]=useState(false);
    const [normal,setnormal]=useState(true);
    const [profiles,setProfiles]=useState([]);
    const [changepassword,setchangepassword]=useState(false);
    const [managerFname,setmanagerFname]=useState('');
    const [manageLname,setmanageLname]=useState('');
    const [companyName,setcompanyName]=useState('');
    const [phoneNo,setphoneNo]=useState('');
    const [region,setregion]=useState('')
    const [message,setmessage]=useState('');
    const [city,setCity]=useState('');
    const [pp,setpp]=useState('');
    const [np,setnp]=useState('');
    const [cp,setcp]=useState('');
    const [auth,setAuth]=useState(false);
    const api=axios.create({
        baseURL:"http://localhost:5000/sel"
      }); 
    const handlechangepp=(e)=>{
        e.preventDefault();
        setmessage("...loading")
        if(adfname===''){
            setadfname( profiles[0].aFname)
        } 
        if(adlname===''){
            setadlname(profiles[0].aLname);
        }
        if(adtelphone===''){
            setadtelphone(profiles[0].phonenumber);
        }
        if(telUsername===''){
            settelUsername(profiles[0].telUsername);
        }
        console.log(
            adfname,adlname,adtelphone,telUsername
        );
        api.post('/changepp',{
            adfname,adlname,adtelphone,telUsername
        }
      ).then(res=>{
        if(res.status===200)
        {
            setmessage("successfully updated profile")
        }
        }).catch(err=>{
            if (err.response) {
                setmessage("something went wrong");
            } else if (err.request) {
                setmessage("something went wrong");
            } else {
                // Anything else
            
                nav('/adlogin') 
                
            }
      });

    }
    const handlechangepassword=(e)=>{
        e.preventDefault();
        setmessage("...loading")
        if(np!==cp){
            setmessage("password should be the same ")
            return;
        }
        api.post('/changepassword',{
            pp,np,cp
        }
      ).then(res=>{
        if(res.status===200)
        {
           setmessage("successfully updated")
        }
        }).catch(err=>{
            if (err.response) {
                setmessage("wrong password");
                console.log(err);
        } else if (err.request) {
                setmessage("you are currently offline")

      } else {
          // Anything else
          console.log(err);
          nav('/adlogin') 
        
      }
      });

    }
    // useEffect((nav)=>{
    //     api.post('/getprofilea',
    //   ).then(res=>{
    //     setAuth(true);
    //     if(res.status===200)
    //     {
    //         let received=[];
    //         received.push(res.data);
    //         setProfiles({...received});
    //         // console.log("received",received)
    //         // console.log("profiles",profiles)
    //         // console.log(profiles[0]);
    //     }
    //     }).catch(err=>{
    //         if (err.response) {
    //             nav('/adlogin') 
    //             console.log(err);
    //     } else if (err.request) {
    //         nav('/adlogin') 

    //   } else {
    //       // Anything else
    //       console.log(err);
    //       nav('/adlogin') 
        
    //   }
    //   });
    
    //   },[normal])
    return ( 
    <>
    {auth &&<>
     <NavbarSeller/>
