import React,{ useState }  from 'react';
import NavbarSeller from './navbarSeller';
import { TextField,MenuItem,Select,InputLabel,Box,Typography } from '@mui/material';
import FormControl from '@mui/material/FormControl';
import axios from "axios";
// import { useForm } from "react-hook-form";
import MultiImageInput from "react-multiple-image-input";
//----///
const serv=axios.create({
  baseURL:"http://localhost:5000/sel",
});
//headers:{"Content-Type":"multipart/formdata"}
const AddProduct = () => {
  const [image, setImage] = useState({ preview: '', data: '' })
  const [status, setStatus] = useState('')
  const [category, setCategory] = React.useState('');
  const handleChange = (event) => {
      setCategory(event.target.value);
  };
  const handleFileChange = (e) => {
    const img = {
      preview: URL.createObjectURL(e.target.files[0]),
      data: e.target.files[0],
    }
    setImage(img)
  }
  const handleSumbit=(e)=>{
    console.log("hello")
    e.preventDefault();
    let formData = new FormData()
    formData.append('file', image.data)

    serv.post('/upload',
     { body:formData}
    ).then(res=>{
      console.log(res);
    }).catch(err=>{
      if (err.response) {
        console.log("response error");
     
    } else if (err.request) {
        // The client never received a response, and the request was never left
        console.log(" request error something went wrong ");
    } else {
        // Anything else
        console.log("something went wrong ");
    }
    });
    
}
  const crop = {
    unit: "%",
    aspect: 4 / 3,
    width: "100"
  };
    return ( 
    <div>
        <NavbarSeller/>
        <Box sx={{
            position:"absolute",
            left:"10%",
            top:"10%",
            width:"80%",
            backgroundColor:"white",

        }}>
            <Typography variant="h4">AddProduct</Typography>
        <form onSubmit={handleSumbit}>
        <TextField id="outlined-basic" label="name" variant="outlined" />
        <br/>
        <br/>
        <TextField id="outlined-basic" label="price" variant="outlined" />
        <br/>
        <br/>
        <FormControl fullWidth >
        <InputLabel id="demo-simple-select-label">category</InputLabel>
        <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={category}
            label="category"
            onChange={handleChange}
        >
          <MenuItem value="cosmotics">cosmotics</MenuItem>
          <MenuItem value="shoes">shoes</MenuItem>
          <MenuItem value="laptop">laptop</MenuItem>
        </Select>
        <br/>
        <br/>
        </FormControl>
        
        <input type='file' name='file' onChange={handleFileChange}></input>
        {image.preview && <img src={image.preview} width='150px' height='150px' />}
        <button type="submit">Post</button>
        </form>
        
        </Box>

    </div>
     );
}

export default AddProduct;
