let length=3;
  var width = window.innerWidth;
const y= (x)=>{
  if(x<650) return 2;
  else  return 4;
}
const z= (x)=>{
  if(x<650) return "170px";
  else  return "300px";
}
const CompPro = () => {
    const nav=useNavigate();
    const [page, setPage] = React.useState(1);
    const [loading,setloading]=useState(true);
    const [nopage,setnopage]=useState(1);
    const [itemData,setItemData]=useState([]);
    const api=axios.create({
      baseURL:"http://localhost:5000/company"
    })
    const api1=axios.create({
      baseURL:"http://localhost:5000/custom"
    })
    const handleaddCart=(itemid)=>{
        console.log("The item id is  ",itemid);
          // api.post('/addcart',{
          //   withCredentials:true
          // },
          // )
          // .then(res=>{
    
          // }).catch(err=>{
          //   if(err.response.status==404){
          //     nav('/')
          //   }
          //   else{
          //     console.log(err);
          //   }
          // })
      }
    const handleChange = (event, value) => {
      setPage(value);
    };
    let items=[];
    const searchquery=useParams();
    let searchitem=searchquery.searchitem;
    useEffect(()=>{
        api.get(`/?cpname=${searchitem}&page=${page}`,{
            withCredentials:true
          },
          )
          .then(res=>{
            if(res.status===200){
              let bd=res.data;
              items=bd;
              setnopage(bd.count+1);
              setItemData([...bd.rows]);
              // setfind(true);
              console.log("items",itemData)
            }
          }).catch(err=>{
            console.log(err);
            
          });
          setloading(false);

    },[page])
    console.log("search",searchquery);
    
        return ( 
        <Box >
        <Navbar/>
        <Box flex={6}>
        <Box sx={{
                position:"relative",
                marginTop:"50px",
            }}>
        {!loading &&<ImageList cols={y(width)}  sx={{ position:"relative",
    }}>
    {itemData.length>0 && <ImageList cols={y(width)}  sx={{ 
        position:"relative", 
    }}>
        {itemData.map((item) => (
          <Box key={item.pid} sx={{
            borderColor:"red",
            borderWidth:"1px",
            width:z(width),
            }}>
        <Link href={`/details/${item.pid}`}>
            <ImageListItem  >
                <img
                    src={`http://localhost:5000/images/${item.letmeSee}`}
                    alt={item.pname}
                    loading="lazy"
                    style={{
                      backgroundColor:"gray",
                      height:"200px",
                  }}
                />
        </ImageListItem>
        </Link>
        <p style={{backgroundColor:"white",
          maxHeight :"110px",
          width:z(width),
          overflow: "hidden",
          textOverflow:"..."
            }}>{item.pname}
            </p>
        <Stack direction={"row"} gap={2}>
          <p style={{color:"green"}}>{item.price}</p>
          <p style={{color:"red"}}> <del>{item.marketprice}</del></p>
          <Button sx={{
            height:"30px"
          }} variant="contained" startIcon={<ShoppingCart />}
          onClick={()=>{
            handleaddCart(item.pid)
          }}
          >
          Add
          </Button>
        </Stack>
        
        </Box>
      ))}
    </ImageList>}
    </ImageList>}
    {
      
      (itemData.length===0 && !loading) && <div> Our beloved customers, the product we have for know is this please keep in touch we are adding other products ,
        please  enjoy  other products !</div>
    }
    {
      loading && <CircularProgress/>
    }
    </Box>
    <div style={{
        position:"relative",
        marginTop:"10px",
        backgroundColor:"yellow",
      }}> <Pagination count={nopage} color="primary" page={page} onChange={handleChange} sx={{position:"relative" }}/>
      </div>
        </Box>
        </Box>
    );
}
